css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("numerama.com") {

#ratbody { margin: auto !important; }

#header_content > div > div > div:first-child > div:first-child,
#header_search,
#header_height,
#header_pub,
#tipflattr,
#general_content > table > tbody > tr > td:nth-child(2),
#general_content > table > tbody > tr > td:first-child > div > div > div:nth-child(5) > div:first-child,
#general_content > table > tbody > tr > td:first-child > div > div > div:nth-child(10),
#general_content > table > tbody > tr > td:first-child > div > div > div:nth-child(11),
#menunumbloc > :not(.firstlevel),
#menunumbloc > .transeffect.comp,
#statusbar_bg_center > :last-child,
#general_dec,
#general_content > div:not(#ramafooter)
{
  display: none !important;
}

#header_content { padding-top: 5px !important; }

#header_menu { padding-top: 0 !important; clear: both !important; }

#statusbar_general { margin: 0 !important; }

#statusbar_bg_center > div { padding: 0 !important; }

#status_login > :first-child { margin: 0 0 5px 0 !important; }

#general_content > table {
  width: 700px !important;
  margin: 10px auto 0 !important;
  clear: both;
}

}
