css:
/*
This stylesheet combines many of my separate styles together.
It is my own personal configuration, you may not like certain things in it.
Do not install other styles of mine alongside it.
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.ign.com"), domain("betaboards.ign.com"), domain("forums.ign.com") {


/*
MINIMALIZER
*/


body {
padding-bottom: 50px !important;
background-position: 0px 36px !important;
}

#mast_logo,
#search_tools,
#nav_special,
#nav_top,
#boards_full_width_ad_container,
#boards_footer,
.networkBar,
#network-imprint,
.img-masthead,
#lnk-avatar,
#lnk-logout-avatar,
#masthead-search,
footer {
display: none !important;
}

#masthead {
height: 20px !important;
}

#header-boards {
min-width: 1169px !important;
}

#header-boards > .wrapper {
width: auto !important;
}

nav {
width: auto !important;
margin: 0 150px !important;
}

nav > ul {
margin: 0 auto !important;
float: left !important;
}

li.nav-item.home {
position: absolute !important;
margin-left: 50px !important;
}

li.nav-item.home .bot-bub {
height: 9px !important;
}

#nav-home {
margin-right: 60px !important;
}

#masthead-tools {
margin-top: 1px !important;
height: auto !important;
width: 100% !important;
position: absolute !important;
}

.account-actions,
.register-actions {
position: static !important;
margin-left: 1% !important;
margin-top: 1px !important;
width: 151px !important;
}

.account-actions > li:last-child {
width: auto !important;
}

#username {
max-width: 145px !important;
overflow-x: hidden !important;
}

#boards-pm {
float: right !important;
width: auto !important;
}

#header-boards {
height: auto !important;
background: #000000 url(http://www.ign.com/static/2010/ign_masthead_rpt_default.png) repeat-x scroll 0 -18px !important;
position: static !important;
}

#header-boards .content {
height: auto !important;
float: none !important;
}

#nav-global {
height: auto !important;
float: none !important;
position: static !important;
width: 975px !important;
}

#account_tools {
margin-top: 2px !important;
}

#logo_beta {
left: 0px !important;
top: 6px !important;
}

#boards_full_width_user_search,
#boards_full_width_user_search_webforms,
#boards_main_user_search {
min-height: 5px !important;
height: auto !important;
}

#boards_breadcrumb {
padding: 2px 0 0 !important;
margin: 0 5px 2px !important;
float: left;
width: auto !important;
}

#boards_user_profile_container {
height: 130px !important;
float: left;
clear: left;
}

#boards_search_container,
#boards_search_container.searchTweak {
margin: 10px 5px 0 0 !important;
background: #b3b1bf !important;
border: 1px solid #65656d;
height: 40px !important;
width: auto !important;
}

#boards_search_container form {
float: left !important;
}

#boards_search_input,
#boards_search_container.searchTweak #boards_search_input {
margin: 2px 0 0 2px !important;
width: 387px;
}

#boards_search_submit,
#boards_search_container.searchTweak #boards_search_submit {
background: #b1b3bc url(http://media.ignimgs.com/boards/img/grey/google_search_bg.jpg) 79px 48px;
margin-top: 2px !important;
width: 67px !important;
margin-right: 1px !important;
}

.searchTweak #wikiSearchButton {
margin: 2px 2px 0 -2px !important;
}

#boards_search_container h1 {
display: none !important;
}

#boards_full_width_user_search > div.clear:nth-child(7) {
display: none !important;
}

#boards_announcement_dropshadow {
margin: 7px 1px 4px !important;
width: auto !important;
}

#boards_announcement_container {
width: auto !important;
min-width: 200px !important;
}

#boards_main_user_search > h1 {
font-size: 11px !important;
padding-top: 2px !important;
}

#boards_main_sidebar > #boards_medrec_relative {
display: none !important;
}

#boards_sidebar_wrapper:nth-child(2) {
margin-top: 50px !important;
}

#boards_featured_header {
padding-top: 5px !important;
margin-bottom: 5px !important;
}

.boards_favorites_header {
margin-top: 5px !important;
}

.boards_main_section_wrapper > h1 {
margin-top: 10px !important;
}

.boards_main_boards_row {
padding-top: 3px !important;
padding-bottom: 3px !important;
}

.boards_main_boards_name > a > img {
height: 15px !important;
width: 15px !important;
}

#boards_board_list_header,
#boards_board_list_footer {
position: relative;
height: auto !important;
min-height: 30px !important;
}

#boards_board_list_header h1 {
display: inline !important;
float: right;
font-size: 20px !important;
margin: 0 8px 0 0 !important;
padding-top: 2px !important;
}

#boards_board_list_header .boards_board_list_buttons,
#boards_board_list_footer .boards_board_list_buttons {
margin-top: 6px !important;
margin-bottom: 2px !important;
height: auto !important;
}

#boards_pagination_wrapper,
#boards_bottom_pagination_wrapper {
height: 25px !important;
position: relative !important;
}

.boards_pagination {
margin-top: 3px !important;
display: inline !important;
float: left !important;
}

.boards_pagination_dropdown {
display: inline !important;
position: static !important;
float: right;
margin: 4px 2px 0px 2px !important;
}

.boards_pagination a, .boards_pagination a:visited {
padding: 1px 5px !important;
}

.boards_pagination .disablelink, .boards_pagination .disablelink:hover {
padding: 1px 5px !important;
}

#boards_board_list_table_header {
padding-top: 3px !important;
height: 20px !important;
}

#boards_board_list_header_subject {
width: 560px !important;
}

.boards_board_list_row,
.boards_board_list_row_over {
padding: 0 0 2px 0 !important;
}

.boards_board_list_row_icon,
.boards_board_list_row_icon img {
padding: 0 0 0 0 !important;
height: 22px !important;
}

.boards_board_list_row_subject {
padding: 3px 0 0 0 !important;
width: 560px !important;
}

.boards_board_list_row_username,
.boards_board_list_row_replies,
.boards_board_list_row_lastpost {
padding: 4px 0 0 0 !important;
}

.boards_board_list_row_subject a {
font-size: 10pt !important;
font-family: verdana, arial, sans-serif !important;
}

.boards_board_list_row_subject .boards_mini_paging {
display: inline !important;
margin-left: 5px;
}

.boards_board_list_row_subject .boards_mini_paging a {
font-size: 7pt !important;
}

#boards_thread_header_info {
height: 0 !important;
padding: 0 !important;
border: none !important;
margin: 0 !important;
}

#boards_thread_header_info h2, 
#boards_thread_header_info h1, 
#boards_thread_header_info div {
display: inline !important;
float: left;
clear: left;
}

#boards_thread_header_info h2 {
clear: right;
}

#boards_thread_header_info h2 {
opacity: 0.5 !important;
position: relative !important;
top: 4px;
margin-right: 4px !important;
float: right !important;
}

#boards_thread_header_links {
height: 25px !important;
border-top-width: 0 !important;
}

#boards_thread_header_links .boards_board_list_buttons {
margin: 5px 0 0 0 !important;
height: 20px !important;
width: 180px !important;
}

#boards_thread_header_links .boards_board_list_buttons:first-child {
margin-left: 50px !important;
width: 130px !important;
}

#boards_thread_header_links .boards_thread_topic_buttons {
margin: 6px 0 0 10px !important;
}

.boards_thread_row {
margin-top: 15px !important;
}

.boards_thread_links {
padding-bottom: 6px !important;
}

#boards_poll_header {
padding: 3px 3px 0 5px !important;
}

#boards_poll_question {
padding: 3px 3px 0 5px !important;
margin-bottom: 7px !important;
}

.boards_poll_choice_row,
.boards_poll_result_radio_choice,
.boards_poll_result_answer {
height: auto !important;
}

.boards_poll_vote_button {
display: inline !important;
float: left;
}


/*
FLUID WIDTH
*/


#boards_container,
#masthead {
width: auto !important;
margin: 12px 1% !important;
min-width: 976px !important;
}

#nav_top > .container {
background: transparent url(http://media.ignimgs.com/boards/img/classic/nav_bg.jpg) repeat-x scroll 0 0 !important;
}

#nav_end {
background: transparent url(http://media.ignimgs.com/boards/img/classic/nav_bookends.jpg) no-repeat scroll -10px 0 !important;
float: right !important;
height: 29px !important;
width: 10px !important;
}

#nav_front {
background: transparent url(http://media.ignimgs.com/boards/img/classic/nav_bookends.jpg) no-repeat scroll 0 0 !important;
height: 29px !important;
width: 10px !important;
}

#boards_main_content {
position: relative;
background-color: #706e7b !important;
background-position: top right !important;
min-height: 1300px !important;
}

#boards_main_body_container {
width: auto !important;
margin-right: 308px;
float: none !important;
}

#boards_main_body_container #boards_full_width_user_search {
margin-right: 10px !important;
}

#boards_main_sidebar {
background-color: #838290 !important;
position: absolute;
right: 0;
top: 0;
}

#boards_medrec_relative {
display: none !important;
}

#boards_sidebar_wrapper:nth-child(2) {
margin-top: 10px !important;
}

#boards_main_user_search {
width: auto !important;
margin-right: 10px !important;
background-repeat: repeat-x !important;
}

.boards_main_section_wrapper {
width: auto !important;
margin-right: 10px;
float: none !important;
}

.boards_favorites_header {
width: auto !important;
margin-left: 25px !important;
margin-right: 25px !important;
}

.boards_favorites_header h1 {
width: 60% !important;
}

.boards_main_boards_row {
width: 98% !important;
}

.boards_main_boards_name {
width: 57% !important;
}

.boards_main_boards_header {
width: 97.5% !important;
}

.boards_main_boards_header h1 {
width: 60% !important;
}

.boards_main_boards_dividing_line {
width: 95% !important;
}

#boards_category_header {
width: 100% !important;
}

#boards_category_header h1 {
width: 57% !important;
}

#nav_top .container,
#nav_special,
#nav_channel .container,
#navigation_content_gutter,
#boards_full_width_user_search,
#boards_breadcrumb,
#boards_breadcrumb_bottom,
#boards_full_width_user_search_webforms,
#boards_webform_wrapper,
#boards_full_width,
#boards_board_list_header {
  width: auto !important;
  }

#boards_full_width_ad_container {
display: none !important;
}

#status_message,
#boards_pagination_wrapper,
#boards_bottom_pagination_wrapper,
#boards_board_list_table_header,
.boards_board_list_row, 
.boards_board_list_row_over,
#boards_board_list_footer,
#boards_additional_info {
width: 100% !important;
}

#boards_board_list_footer {
padding-left: 0px !important;
}

#boards_board_list_footer > div:first-child {
padding-left: 24px !important;
}

#status_message {
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
height: 35px !important;
background: #c1c2c9 !important;
border-style: solid;
border-width: 1px 0px;
border-color: #95959d;
}

#boards_pagination_wrapper {
position: relative !important;
}

.boards_pagination {
position: absolute;
left: 0%;
right: 100px;
}

.boards_pagination_dropdown {
display: inline !important;
position: static !important;
float: right;
margin: 4px 2px 0px 2px !important;
height: 20px !important;
}

#boards_board_list_table,
#boards_board_list_table_header {
position: relative;
}

.boards_board_list_row,
.boards_board_list_row_over {
position: relative;
}

.boards_board_list_row_icon {
position: absolute;
left: 0;
top: 0;
z-index: 1;
}

#boards_board_list_header_icon {
position: absolute;
}

.boards_board_list_row_subject {
padding-top: 3px !important;
padding-bottom: 4px !important;
}

.boards_board_list_row_subject a:first-child:empty {
position: relative;
right: 20px;
z-index: 0;
}
.boards_board_list_row_subject a:first-child:empty:after {
content: "\00a0";
}

.boards_board_list_row_subject,
#boards_board_list_header_subject {
margin-left: 45px;
margin-right: 393px;
width: auto !important;
min-width: 527px !important;
}

.boards_mini_paging {
display: inline !important;
margin-left: 5px;
}

.boards_board_list_row_username,
#boards_board_list_header_author {
position: absolute;
right: 245px;
}

.boards_board_list_row_replies,
#boards_board_list_header_replies {
position: absolute;
right: 195px;
}

.boards_board_list_row_lastpost,
#boards_board_list_header_lastactivity {
position: absolute;
right: 15px;
}

.boards_thread_row {
width: 100% !important;
position: relative !important;
}

.boards_thread_post_container {
float: left !important;
position: relative;
width: 78% !important;
}

.boards_thread_date {
float: left !important;
}

.boards_thread_subject {
width: auto !important;
}

.boards_thread_post_wrapper {
width: 100% !important;
}

.boards_thread_links {
float: none !important;
width: auto !important;
margin: 0 14px !important;
}

.boards_quick_post_inline {
position: relative !important;
}

#boards_quickpost_wrapper {
width: auto !important;
margin: 0 5px !important;
}

#quickPostStartContainer {
width: auto !important;
}

#boards_breadcrumb_bottom {
margin: 0 10px !important;
}

#site_map .columns {
margin: 0 auto;
}


/*
OLD LAYOUT THREADS
*/


#boards_full_width {
margin: 0 4px !important;
padding: 0 !important;
}

#boards_bottom_pagination_wrapper {
border: 0px solid #838291 !important;
border-width: 0px 4px !important;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

#boards_board_list_footer + #boards_bottom_pagination_wrapper {
border-color: #ccc !important;
}

.boards_thread_row {
background-color: #b1b3bc !important;
margin-top: 0px !important;
border: 0px solid #ccc !important;
border-top-width: 1px !important;
position: relative !important;
}

.boards_thread_bubble_wrapper {
display: none !important;
}

.boards_thread_post_wrapper {
background: #c1c2c9 !important;
width: 100% !important;
}

.boards_thread_user_profile_container {
padding-top: 78px !important;
padding-bottom: 10px !important;
width: 17% !important;
position: relative !important;
border: 0px solid #cccccc;
border-right-width: 1px;
}

.boards_thread_post_container {
width: 82.7% !important;
margin-right: 0 !important;
position: relative !important;
right: 1px;
border: 0px solid #ccc !important;
border-left-width: 1px !important;
}

.boards_thread_post_wrapper {
border: 0px solid #ccc !important;
border-top-width: 1px !important;
}

.boards_thread_post {
padding: 10px 9px 10px 9px !important;
position: relative;
/*Next rule tries to fill out extra space with the post*/
/*can make pages substantially longer so you may want to disable it*/
min-height: 143px;
}

.boards_thread_user_profile_icon_80,
.boards_thread_user_profile_icon_120 {
background: #b1b3bc !important;
float: left !important;
width: auto !important;
height: auto !important;
margin-left: 22px !important;
}

.boards_thread_user_profile_icon_80 img,
.boards_thread_user_profile_icon_120 img {
padding: 0 !important;
}

.boards_thread_user_profile_info {
width: auto !important;
height: auto !important;
text-align: left !important;
margin: 0 9px 5px 9px;
clear: left;
position: absolute;
top: 3px;
color: black !important;
}

.boards_thread_user_profile_info a {
font-size: 10pt;
}

.boards_thread_user_profile_stars {
position: relative !important;
float: left !important;
bottom: 10px;
left: 6px;
opacity: 0.5;
}

.boards_thread_user_profile_infobox,
.boards_thread_user_profile_infobox_120 {
margin-left: 31px !important;
background: rgba(133,134,141, 0.75) !important;
width: auto !important;
height: auto !important;
min-width: 100px !important;
border: 1px solid rgba(102, 102, 102, 0.75) !important;
border-left-width: 0px !important;
-moz-box-shadow: 5px 6px 4px 0 rgba(0,0,0, 0.25),
                 inset 4px 5px 5px 0 rgba(177,179,188, 0.9),
                 inset 70px 0px 50px -50px rgba(178,179,188, 0.9);
-moz-border-radius: 50px 0 0 50px/ 1px 0 0 1px;
opacity: 0.9;
}

.boards_thread_user_profile_infobox {
min-height: 73px !important;
left: 90px !important;
}

.boards_thread_user_profile_infobox_120 {
min-height: 113px !important;
left: 130px !important;
margin-top: -7px !important;
}

.boards_thread_subject,
.boards_thread_date,
.boards_thread_post_wrapper {
margin-left: 0 !important;
padding: 0 !important;
}

.boards_thread_subject,
.boards_thread_date {
margin: 2px 2px 2px 4px !important;
color: black !important;
}

.boards_thread_subject {
margin-left: 200px !important;
}

.boards_thread_date {
width: auto !important;
position: absolute !important;
left: 2px;
}

.boards_thread_links {
margin: 0 !important;
padding-right: 20px !important;
border-color: #ccc !important;
text-align: right !important;
font-size: 10pt !important;
height: auto !important;
min-height: 17px !important;
}

.boards_thread_links a {
font-family: verdana, arial, sans-serif !important;
}

.boards_thread_links b {
display: none !important;
}

.boards_thread_links a[href*="Reply"]:not([href*="quote"]):before {
content: "Post ";
}

.boards_thread_links a[href*="quote"]:after,
.boards_thread_links a.showReply:after,
.boards_thread_links a.hideReply:after {
content: " Reply";
}

.boards_thread_links a[href*="Edit"]:after {
content: " Reply";
}

.boards_thread_links .ignoreUser:after,
.boards_thread_links .unIgnoreUser:after {
content: " User";
}

.hiddenReply .boards_thread_user_profile_container {
padding-top: 3px !important;
padding-bottom: 3px !important;
}

.hiddenReply .boards_thread_user_profile_info {
position: static !important;
}

.hiddenReply .boards_thread_post_wrapper {
min-height: 66px;
}

.hiddenReply .boards_thread_links {
margin-top: 37px !important;
}


/*
OLD LAYOUT BOARDS
*/


#boards_board_list_table_header {
background: #999999 !important;
height: auto !important;
padding: 0 !important;
color: white !important;
font-size: 10pt !important;
font-family: verdana, verdana, sans-serif !important;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
border: 5px solid #cccccc !important;
border-width: 2px 5px 1px 5px !important;
}

#boards_board_list_header_subject {
margin-left: 7px !important;
padding: 4px 0 !important;
}

#boards_board_list_header_author {
right: 407px !important;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
height: 100% !important;
border: 0 solid #cccccc !important;
border-width: 0 0 0 1px !important;
padding: 4px 0 !important;
width: 55px !important;
padding-left: 5px !important;
text-align: left !important;
}

#boards_board_list_header_replies {
padding: 4px 0 !important;
padding-left: 5px;
width: 67px !important;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
height: 100% !important;
border: 0 solid #cccccc !important;
border-width: 0 0 0 1px !important;
right: 198px !important;
}

#boards_board_list_header_lastactivity {
padding: 4px 0 !important;
right: 58px !important;
}

#boards_board_list_header_lastactivity:before {
content: "& ";
}

#boards_board_list_table {
background: #cccccc !important;
}

.boards_board_list_row,
.boards_board_list_row_over {
padding: 0 0 1px 0 !important;
border: 5px solid #cccccc !important;
border-width: 0px 5px 1px 5px !important;
background: #c1c2c9 !important;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

.boards_board_list_row_over {
background: #d1d2d9 !important;
}

.boards_board_list_row_over .boards_board_list_row_username,
.boards_board_list_row_over .boards_board_list_row_lastpost {
background: #c1c3cc !important;
}

.boards_board_list_row_icon, .boards_board_list_row_icon img {
height:22px !important;
padding:0 !important;
width: auto !important;
z-index: 1;
}

/*.boards_board_list_row_icon img[src$="icon_board_open.png"] {
background: url("http://www.chp.edu/chp_core/css/content_bullet.gif");
width: 0 !important;
height: 0 !important;
padding: 5px 0 0 7px !important;
}*/

.boards_board_list_row_subject {
margin-left: 25px !important;
margin-right: 463px !important;
min-width: 480px !important;
padding: 3px 0 4px 0 !important;
}

.boards_board_list_row_subject a:first-child {
color: #009 !important;
font-family: verdana, arial, sans-serif !important;
font-size: 10pt !important;
}

/*add content to empty subject links so the row doesn't collapse,
but move the link relative so it's under the icon and still unclickable,
but able to be tabbed to unlike visibility: hidden*/

.boards_board_list_row_subject a:first-child:empty {
position: relative;
right: 20px;
z-index: 0;
}
.boards_board_list_row_subject a:first-child:empty:after {
content: "\00a0";
}

.boards_mini_paging > a:first-child {
font-size: 10px !important;
color: #039 !important;
}

.boards_board_list_row_username {
background: #b1b3bc !important;
text-align: left !important;
padding: 5px 0 1px 3px !important;
-moz-box-sizing: border-box !important;
-webkit-box-sizing: border-box;
box-sizing: border-box;
width: 198px !important;
height: 100% !important;
font-family: verdana,arial,sans-serif !important;
font-size: 10pt !important;
right: 264px !important;
border: 0 solid #ccc;
border-width: 0 1px 0 1px !important;
}

.boards_board_list_row_replies {
text-align: left !important;
width: 60px !important;
font-family: verdana, arial, sans-serif !important;
font-size: 10pt !important;
font-weight: normal !important;
right: 199px !important;
padding: 4px 0 3px 0 !important;
}

.boards_board_list_row_lastpost {
text-align: left !important;
font-size: 7pt !important;
font-family: verdana, arial, sans-serif !important;
color: black !important;
background: #b1b3bc !important;
padding: 7px 0 0 3px !important;
-moz-box-sizing: border-box !important;
-webkit-box-sizing: border-box;
box-sizing: border-box;
height: 100% !important;
width: 201px !important;
right: 0 !important;
border: 0 solid #ccc;
border-width: 0 0 0 1px !important;
}

.boards_board_list_row_lastpost a:before {
content: " by ";
color: black !important;
background: transparent !important;
font-weight: normal !important;
border: none !important;
font-style: normal !important;
text-decoration: none !important;
}


/*
NO SUBJECT CLICKABLE
*/


.boards_board_list_row_subject a:empty:after {
content: "<no subject>" !important;
}

.boards_board_list_row_subject a:empty {
position: static !important;
}


/*
HIDE POST STARS
*/


#boards_user_profile_stars,
.boards_thread_user_profile_stars,
.BoardStarLink {
display: none !important;
}


/*
SMALL ICON
*/


#boards_full_width_user_search {
min-height: 5px !important;
}

#boards_user_profile_stars,
#boards_user_profile_username {
display: none !important;
}

#boards_user_profile_container,
#boards_user_profile_icon {
width: auto !important;
height: auto !important;
clear: none !important;
background: none !important;
}

#boards_user_private_messages {
top: 25px !important;
left: 0px !important;
}

#boards_user_profile_icon img {
width: 40px !important;
height: 40px !important;
padding: 2px !important;
border: 1px solid #676570 !important;
background-color: #c1c2c9 !important;
}

#boards_user_profile_infobox {
margin-top: 25px !important;
margin-left: 9px !important;
background: rgba(193,194,201, 0.9) !important;
border: 1px solid rgba(103,101,112, 1) !important;
border-left-width: 0px !important;
-moz-box-shadow: 5px 6px 4px 0 rgba(0,0,0, 0.25),
                 inset 4px 5px 5px 0 rgba(193,194,201, 1);
-moz-border-radius: 0 0 0 50px/ 0 0 0 1px;
z-index: 10;
}


/*
TINY SEARCH BAR
*/


#boards_search_container,
#boards_search_container.searchTweak {
margin: 10px 5px 5px 5px !important;
background: #b3b1bf !important;
border: 1px solid #65656d !important;
min-height: 25px !important;
height: auto !important;
width: auto !important;
padding-bottom: 2px !important;
}

#boards_search_container form,
#boards_search_container input[type="submit"] {
float: left !important;
}

#boards_search_container #boards_search_input,
#boards_search_container.searchTweak #boards_search_input {
margin: 2px 0 0 2px !important;
width: 160px !important;
height: 17px !important;
padding: 2px !important;
font-size: 14px !important;
}

#boards_search_container #boards_search_submit,
#boards_search_container.searchTweak #boards_search_submit {
background: #b1b3bc url(http://media.ignimgs.com/boards/img/grey/google_search_bg.jpg) 79px 41px !important;
margin-top: 2px !important;
width: 65px !important;
margin-right: 2px !important;
height: 21px !important;
border: 1px solid #99989d !important;
border-width: 1px 1px 1px 0px !important;
}

#boards_search_container h1 {
display: none !important;
}

.searchTweak #wikiSearchButton {
margin: 2px 2px 0 -2px !important;
height: 23px !important;
}


/*
SIGNATURE FORMATTING
*/


.boards_signature_title, 
.boards_thread_post_signature,
.boards_thread_post > img:last-child:not(.BoardFace) {
position: relative;
top: 8px;
}

.boards_thread_post_signature {
color: #888 !important;
}

.boards_thread_post_signature a {
color: #77b !important;
}

.boards_thread_post_signature .BoardFace {
opacity: 0.5 !important;
}


/*
RECENT POSTS HEIGHT FIX
*/


/*recent posts expands*/
#boards_add_info_my_recent_posts {
height: auto !important;
min-height: 230px !important;
}

/*additional info expands*/
#boards_additional_info {
height: auto !important;
min-height: 225px !important;
}


/*
BREAK LONG WORDS
*/


.boards_thread_post {
word-wrap: break-word !important;
}


/*
HIDE JPEGS
*/


.boards_thread_user_profile_icon_80 img[src$=".jpg"],
.boards_thread_user_profile_icon_120 img[src$=".jpg"],
.boards_thread_post > img[src$=".jpg"]:last-child {
opacity: 0;
}

.boards_thread_user_profile_icon_80 img[src$=".jpg"]:hover,
.boards_thread_user_profile_icon_120 img[src$=".jpg"]:hover,
.boards_thread_post > img[src$=".jpg"]:last-child:hover {
opacity: 1;
}


/*
OLD POLL LAYOUT
*/


#boards_poll_wrapper {
width: 378px !important;
border: none !important;
font-family: Verdana;
font-size: 10pt !important;
letter-spacing: 0 !important; 
}

#boards_poll_header {
background: #999 !important;
height: 16px !important;
padding: 2px 2px 2px 26px !important;
}

#boards_poll_question {
display: none !important;
}

#boards_poll_result_wrapper,
#boards_poll_result_wrapper_alt {
margin: 0 !important;
width: auto !important;
position: relative;
padding-left: 0 !important;
background: #c1c2c9 !important;
padding-top: 0 !important;
padding-bottom: 0 !important;
height: auto !important;
border: 0px solid #999;
border-left-width: 25px !important;
}

.boards_poll_choice_row {
width: auto !important;
height: auto !important;
}

.boards_poll_result_radio_choice {
position: absolute;
left: -25px;
top: 0px;
background: #999;
width: 25px !important;
height: 100% !important;
}

.boards_poll_result_radio_choice input {
margin: 6px 7px !important;
}

.boards_poll_result_answer {
margin-top: 26px;
padding: 2px !important;
width: auto !important;
font-size: 10pt !important;
font-weight: bold;
}

.boards_poll_results_bar_display {
height: 22px !important;
background: #b1b3bc;
width: 310px !important;
position: absolute;
top: 0;
padding: 2px;
}

#boards_poll_result_wrapper img {
height: 0 !important;
border: 5px solid blue !important;
border-width: 11px 0 !important;
}

#boards_poll_result_wrapper img:after {
content: attr(width);
}

#boards_poll_result_wrapper_alt img {
height: 0 !important;
border: 5px solid red !important;
border-width: 11px 0 !important;
}

#boards_poll_result_wrapper_alt img[src*="data"],
#boards_poll_result_wrapper img[src*="data"] {
border-color: green !important;
}

.boards_poll_already_voted {
float: right;
padding-top: 18px !important;
}

.boards_poll_vote_button {
padding-left: 30px !important;
padding-top: 13px !important;
float: left;
}

.boards_poll_info {
background: #b1b3bc !important;
border: 0px solid #999;
border-left-width: 25px !important;
}


/*
OLD FONT
*/


.boards_thread_post {
font-family: Verdana;
font-size: 10pt !important;
letter-spacing: 0 !important; 
}


/*
COLORED TEXT BORDERS
*/


.boards_thread_post span[style^="color"] {
border: 1px solid #ddd !important;
border-width: 1px 0px !important;
} 

.boards_thread_post span[style^="color"]:hover { 
border-color: transparent !important; 
}



/*
COLORED TEXT REMOVER
*/


.boards_thread_post span[style^="color"]:hover,
.boards_thread_post span[style^="background-color"]:hover {
