border: 0px !important;
margin-left: 2px;
margin-top: 3px;
}


/*
LIST FORMATTING
*/


.boards_thread_post li,
.boards_thread_post_wrapper li,
.wysiwyg li,

.boards_thread_post ul li,
.boards_thread_post_wrapper ul li,
.wysiwyg ul li,

.boards_thread_post ol li,
.boards_thread_post_wrapper ol li,
.wysiwyg ol li 

{
