css:
@-moz-document domain("Roblox.com") {
body {
    background: #FFF url(https://i.imgur.com/eNh5rGk.png) repeat-x;
}

#topbar {
    border: #6F99CB 1px solid;
    background-image: url(https://i.imgur.com/lc67t40.png);
    height: 72;
    text-align: left;
    vertical-align: bottom;
}

#content {
    position: relative;
    display: block;
    z-index: 1;
    width: 1024px;
}

body {
    margin-bottom: 0px;
    margin-top: 0px;
    font-family: "Verdana", "Comic Sans", "Courier New";
    background-color: #F8FCFF;
}

#loginbar {
    border-top: #000000 0px solid;
    border-right: #000000 0px solid;
    font-size: 12px;
    position: absolute;
    top: 1;
    font-weight: bold;
}
#notifbar {
    border: #6F99CB 1px solid;
    font-size: 15px;
    position: absolute;
    right: 10px;
    top: 0;
    background-color: #FFF;
    color: #fff;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 5px;
    padding-bottom: 5px;
    width: 205px;
    height: 72px;
    text-align: center;
}
img.fullbackground {
    margin: 0 auto;
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
    margin: 0;
    padding: 0;
    width: 100%;
    /* alternative: right:0; */
    opacity: 0;
}

#absolute {
    top: 1px;
    width: 0px;
}

#Content {
    margin: 0 auto;
    padding: 0 10px;
    border-left: 5px solid #DDD;
    border-right: 5px solid #DDD;
    width: 1050px;
    background: #e5f1fd;
}

#topbar center {
    opacity: 0;
    width: 0px;
}

#navbar {
    border-bottom: #6F99CB 1px solid;
    border-right: #6F99CB 1px solid;
    border-left: #6F99CB 1px solid;
    text-align: center;
    font-family: 'Verdana', Verdana, sans-serif;
    font-size: 12pt;
    font-weight: normal;
    line-height: 2em;
    text-decoration: none;
}
#notifbar .wlink {
    color: #6F99CB;
    font-size: 13px;
    font-weight: bold;
}

html body center #content div table tbody tr th {
    font-family: Verdana;
    font-size: 12px;
    font-weight: bold;
    width: 150px;
    text-align: center;
}
}
