css:
@-moz-document domain('spacehey.com', 'im.spacehey.com', 'forum.spacehey.com', 'groups.spacehey.com', 'im.spacehey.com', 'shop.spacehey.com', 'blog.spacehey.com', 'layouts.spacehey.com') {

.col {
    display: table-cell;
    vertical-align: top;
    background-color: #353535;
}
body {
    background-color: #000 !important;
    font-family: verdana, arial, sans-serif, helvetica;
}
.profile .contact, .profile .url-info, .profile .table-section, .setting-section, .home-actions {
    width: 100%;
    border: 2px solid #60A5FA;
    border: 2px solid #000;
    margin: 10px 0;
    background-color: black;
}
.indie-box {
    outline: 1px solid #1e40af;
    border: 8px solid #000;
    margin: 20px 0 10px 0;
    padding: 5px 5px 15px 5px;
    text-align: center;
    font-weight: bold;
    background-color: black;
    color: #1e40af;
}
footer {
    text-align: center;
    font-size: 70%;
    margin: 10px 0 10px;
    padding: 10px 5px;
    background: #000;
    outline: 1px solid #1e40af;
}
.forum-table th, .forum-table td, .groups-table th, .groups-table td, .bulletin-table th, .bulletin-table td {
    border: 1px solid #1e40af;
    padding: 7px;
    background: black;
}
main {
    color: #fff;
    font-size: 80%;
    outline: 1px solid #1e40af;
}
.home-actions {
    background: #DBEAFE;
    background: #000;
    font-weight: bold;
}
main {
    background: black;
    color: black;
    padding: 6px 0px;
    font-size: 80%;
}
.user-home .blog-preview p {
    overflow-wrap: break-word;
    word-break: break-word;
    color: white;
}
.inner {
    outline: 1px solid #1e40af;
    background: #000;
    color: #1e40af;
    padding: 2px;
}
.simple-container {
    margin: 0 auto 30px auto;
    width: 600px;
    padding: 14px;
    max-width: 100%;
    background-color: #6a6969;
    outline: 1px solid #1e40af;
    color: #1e40af;
}
.forum-table th, .forum-table td, .groups-table th, .groups-table td, .bulletin-table th, .bulletin-table td {
    border: 1px solid #1e40af;
    padding: 7px;
    background: black;
    color: white;
}
.groups-table .group-info .group-pic {
    max-width: 90px;
    max-height: 90px;
    vertical-align: bottom;
    background-color: #acacac;
}
.groups-table td.name {
    text-align: left;
    vertical-align: top;
    background-color: #acacac;
}
.forum-table td.reply-count, .groups-table td.member-count {
    font-size: 110%;
    background-color: gray;
}
.chat-box .messages {
    flex: 1 1 0;
    height: 100%;
    overflow-y: auto;
    background-color: black;
}
.chat-item.active {
    background: linear-gradient(180deg, rgba(0, 0, 0, 0.005), rgba(0, 0, 0, 0.045));
    box-shadow: inset -17px 0px 15px -15px rgba(255, 214, 32, 0.3);
    background-color: #00027b;
}
.chat-item:not(:last-child), .chat-header, .options-list a.option:not(:last-child) {
    border-bottom: 1px solid #c9c7c7;
    background-color: #003bc6;
}
.chat-item:hover, .chat-item:active, .options-list a.option:hover, .options-list a.option:active {
    cursor: pointer;
    background: linear-gradient(180deg, transparent, rgba(0, 0, 0, 0.06));
    background-color: rgba(0, 0, 0, 0);
    background-color: #8caa38;
}
.chat-item:last-child {
    padding-bottom: 10px;
    background-color: #0d00a2;
}
.status, .message {
    background: linear-gradient(180deg, #d7c16a, #686666);
    border: 1px solid #999b9b;
    border-radius: 2px;
    box-shadow: rgba(50, 50, 93, 0.15) 0px 2px 5px -1px, rgba(0, 0, 0, 0.21) 0px 1px 2px -1px;
    padding: 0 12px;
    position: relative;
    margin: 15px 0 8px 0;
    color: #00c;
}
.connection-status {
    background: linear-gradient(180deg, #3e3737, #600);
    border: 1px solid #999b9b;
    border-radius: 0 2px 2px 2px;
    width: 100%;
    font-size: 80%;
    box-shadow: rgba(50, 50, 93, 0.15) 0px 2px 5px -1px, rgba(0, 0, 0, 0.21) 0px 1px 2px -1px;
    padding: 6px 8px;
    display: flex;
    flex-direction: row;
    align-items: center;
}
.chat-header, .new-message {
    background: linear-gradient(180deg, #000, #6700a0);
    background-color: rgba(0, 0, 0, 0);
}
.rich-text__text .html-formatter h5 {
    font-family: var(--font-family-base);
    font-weight: normal;
    color: #fff;
    font-size: 0.8em;
}
.wrapper {
    padding-right: 32px;
    padding-left: 32px;
    background-color: #7e7e7e;
    color: white;
}
.page {
    background: #7e7e7e !important;
    width: 810px;
    max-width: 100%;
    margin: 0 auto 10px;
}
main {
  background: black;
  color: white;
  padding: 6px 0px;
  font-size: 80%;
}

}
