css:
@-moz-document domain(dotnetfiddle.net) {
element.style #vsc-initialized {
  background-color: #2d2d30;
}

span.cm-keyword {
  color: #93c763 !important;
}
span.cm-atom {
  color: #93c763 !important;
}
span.cm-variable {
  color: #f1f2f3 !important;
}
span.cm-number {
  color: #ffcd22 !important;
}
span.cm-comment {
  color: #57a64a !important;
}
span.cm-string {
  color: #ec7600 !important;
}
span.cm-operator {
  color: #e8e2b7 !important;
}

.navbar-center-container {
  background: #2d2d30;
}
.name-container {
  background-color: #2d2d30;
}
body {
  color: #ffffff;
  background-color: #1e1e1e;
}
input[type="text"],
textarea {
  color: #ffffff;
  background-color: #343b41;
}
.access-type-container,
.status-line .counters {
  background-color: #2d2d30;
}

.sidebar {
  background-color: #2d2d30;
}
.vsplitbar {
  background-color: #343b40;
}
.hsplitbar {
  background-color: #343b40;
}
.navbar .btn {
  background: #343b40;
}
.container-sponsor {
  height: 0px !important;
  visibility: collapse;
}
.layout-container {
  max-height: 100% !important;
}

.CodeMirror-gutters {
  background-color: #1e1e1e;
}
.CodeMirror-sizer {
  background-color: #1e1e1e;
}
.CodeMirror-linenumber {
  color: #a5a5a5;
}

#output {
  color: #ffffff;
  font-family: "Consolas";
}
.output-container {
  background-color: #000000;
}

.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
  background-color: #474749;
}
.table > thead > tr > th,
.table > tbody > tr > th,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > tbody > tr > td,
.table > tfoot > tr > td {
  background-color: #474749;
}
table.dataTable tr.odd td.sorting_1 {
  background-color: #474749;
}
table.dataTable tr.even td.sorting_1 {
  background-color: #474749;
}

.hint-overloads .hint-overloads-item.active {
  background-color: #1e1e1e !important;
}
.hint-overloads {
  font-family: "Consolas";
  background-color: #1e1e1e !important;
}
.CodeMirror-hint {
  font-family: "Consolas";
  font-size: 9pt;
}
.CodeMirror-focused .CodeMirror-selected {
  background-color: #324143;
}
.CodeMirror-selected {
  background-color: #172e2f;
}
.CodeMirror-cursor {
  border-left: 1px solid #ffffff;
}

CodeMirror-code::-moz-selection {
  color: #324143 !important;
}
CodeMirror-code::selection {
  color: #324143 !important;
}
pre {
  color: #fff;
}
.CodeMirror {
  font-family: "Consolas";
  color: #fff !important;
  background: #1e1e1e;
  font-size: 95%;
  }
}
