css:
@-moz-document domain(kukuku.fun) {
body::after {
    position: fixed !important;
        content: url('http://i.imgur.com/ejT2aBI.png');
    bottom: -3px !important; 
    right: 0px !important;
  opacity:0.9;
  z-index:1;
  width:280px;
}

html { background: #2A2C2B !important; }
body {z-index:0; font: 12px verdana !important; background: #2A2C2B !important; color: #eee !important;  margin: 0 auto !important; padding: 0; }
form {z-index:6; position: relative; left: -14%; width: 68%!important; margin: 0 auto !important; background: #374140; padding: 25px 5px 5px 5px; border-left: 1px solid #222; border-right:0px solid #222;  }



.extButton {
  border: none;
}



#recent-images li{ background: #333 !important; }
#header { width: 100% !important; }
#navtop { font: 12px verdana !important; border-bottom: 1px solid #555; top: 0; position: fixed;
 padding: 0; width: 100% !important; padding: 5px !important; background: #222 !important;
 color: #8899AA !important; text-align: center !important; z-index: 2 }
 
 .postarea { padding: 0 !important;  }
.postarea form div {border: none !important;}
.postarea input{ }
.postarea form { border-bottom: 1px solid #212121; padding-bottom: 18px !important;}
.postarea table { margin-top: -10px; margin-bottom: -10px; }
.postblock { border: 0 !important; background: #222 !important; color: #aaa !important; }
a { color: #efefef !important; font-weight: bold !important; text-decoration: none !important; border: 0 !important; }
a:hover { color: white !important; background: #222 !important; }


div.logo { visibility: hidden; position: absolute; left: -700px; right: 0; }
div.logo font span { visibility: visible; font: bold 15pt verdana !important; color: white; position: fixed; left: 2em; }


div#ctrl-top.desktop{position: relative; left: 20%; width: 260px;}

.reply:target, .reply.highlight{
background-color:#01413b!important;



}

html{
background:#2A2C2B!important;

}

.reply,.replyhl{
background: #4a5853!important;
z-index:14; 
left: 10px;
width: 90%;
border-radius: 10px!important;
margin-bottom: 10px; 
box-shadow: 0 0 2px rgba(0,0,0,0.3)!important;
}

div.reply {
  background-color: #f0e0d6;
  border: 0px solid #D9BFB7;
  border-left: none;
  border-top: none;
  display: table;
  padding: 2px;
}


div.backlink {
color: #F2F2F2!important;
display:block;
width: 99%;

}

img#recaptcha_reload,
img#recaptcha_switch_audio,
img#recaptcha_whatsthis{
box-shadow:none!important;
}

span.subject {
color:#FF8598 !important;
}

img.bottomad,
div#absbot,
hr, 
.center,
div.center.middlead,
div.sideArrows,
strong,
img#toggleMsgBtn,
img.middlead,
span.postertrip,
.click-me{
display:none!important;
}

.bottomCtrl.desktop {
display: none;
  background: none!important;
  padding: 7px!important;
  position:relative; top: 10px; left: -80px;
  border-radius: 5px!important;
  box-shadow: 0 0 2px rgba(0,0,0,0.3)!important;
}
}
