css:
@-moz-document domain("blog.csdn.net") {

  /* 底部登录注册框 */
  .pulllog-box {
    display: none !important;
  }

  /* 右侧广告 */
  #kp_box_476 {
    display: none;
  }

  /* 右下角vip推广 */
  .meau-gotop-box {
    display: none;
  }

  /* 右侧文章推送 */
  .right-item {
    display: none;
  }

  /* 自动展开文章 */
  .article_content {
    height: 100% !important;
  }

  /* 隐藏 阅读全文，收藏，分享 按钮 */
  .hide-article-box {
    display: none;
  }

  /* 最新文章 */
  #asideNewArticle {
    display: none !important;
  }

  /* 个人分类 */
  #asideCategory {
    display: none !important;
  }

  /* 归档 */
  #asideArchive {
    display: none !important;
  }

  /* 热门文章 */
  #asideHotArticle {
    display: none !important;
  }

  /* 最新评论 */
  #asideNewComments {
    display: none !important;
  }

  /* 联系我们 */
  #asideFooter {
    display: none !important;
  }

  /* 左侧轮播广告 */
  .mb8 {
    display: none;
  }

  /* 底部文章推荐 */
  .recommend-box {
    display: none;
  }

  /* 评论栏上方广告 */
  .t0 {
    display: none;
  }

  /* 顶部工具栏 */
  #csdn-toolbar {
    display: none;
  }

  /* 右侧工具栏 */
  .tool-box {
    display: none !important;
  }

  /* 左侧二维码广告 */
  .fourth_column {
    display: none !important;
  }

  /* 隐藏某些博主的自定义图片背景 */
  body {
    background: #f7f7f7 !important;
  }

  /* 隐藏博客之星广告 */
  .blog_star_enter {
    display: none !important;
  }

  /* 隐藏左侧其它内容 */
  #asideNewNps {
    display: none !important;
  }

  .data-info {
    display: none !important;
  }

  .aside-box-footer {
    display: none !important;
  }

  #asideSearchArticle {
    display: none !important;
  }

  /* #asideArchive {
    display: none !important;
    visibility: hidden !important;
  } */

  .csdn-side-toolbar {
    display: none !important;
  }

  #recommendNps {
    display: none !important;
  }

  #toolBarBox {
    display: none !important;
  }

  .blog-footer-bottom {
    display: none !important;
  }

}
