css:
@-moz-document domain(chat.openai.com) {
  .m-auto {
    margin: 5%;
    margin-top: 2%;
    margin-bottom: 2%;
  }

  textarea[data-id] {
    min-height: 48px !important;
  }

  /* Menu Closed */
  .flex.p-4.gap-4.text-base.md\:gap-6.md\:max-w-3xl.md\:py-6.lg\:px-0.m-auto {
    max-width: 100%;
    transform: translateX(1.85%);
  }

  /* Menu Opened */
  .flex.p-4.gap-4.text-base.md\:gap-6.md\:max-w-2xl.lg\:max-w-\[38rem\].xl\:max-w-3xl.md\:py-6.lg\:px-0.m-auto {
    max-width: 88%;
    transform: translateX(4%);
  }

  .text-base, .prose {
    line-height: 1.3;
    padding: 0;
    /* Enable or Disable to apply a custom font of your choice. */
    /*
    font-size: 13.4px;
    font-family: 'Courier Prime', monospace;
    */
  }

  /* Customize font on text box */
  /*
  #prompt-textarea {
    font-size: 0.8rem;
    font-family: 'Courier Prime', monospace;
  }
  */

  /* Customize font on "code box" (snippet code) written and used by ChatGPT */
  /*
  .p-4.overflow-y-auto {
    font-size: 0.8rem;
    font-family: 'Courier Prime', monospace;
  }
  */

  /* Customize font on "code box" (snippet code) written and used by ChatGPT */
  .p-4.overflow-y-auto {
    font-size: 0.8rem;
    font-family: 'Courier Prime', monospace;                                                                                                                                                                                                                                   
  }
                                                                                                                                                                                                                                                                            
  .h-full.dark\:bg-gray-800 {
    padding-bottom: 14vh;
    max-height: 100%;
  }
}
