css:
/* Styling the body element */
body {
  font-family: Arial, sans-serif;
  background-color: #f0f0f0;
  margin: 0;
  padding: 0;
}

/* Styling the header element */
header {
  background-color: #333;
  color: #fff;
  padding: 10px;
}

/* Styling the navigation menu */
nav {
  background-color: #444;
  padding: 5px;
}

nav ul {
  list-style-type: none;
  margin: 0;
  padding: 0;
}

nav li {
  display: inline-block;
  margin-right: 20px;
}

nav a {
  color: #fff;
  text-decoration: none;
  padding: 5px;
}

nav a:hover {
  background-color: #555;
}

/* Styling the main content */
main {
  padding: 20px;
}

/* Styling paragraphs */
p {
  line-height: 1.6;
}

/* Styling headings */
h1, h2, h3 {
  color: #333;
}

/* Styling links */
a {
  color: #007bff;
}

a:hover {
  text-decoration: underline;
}

/* Styling buttons */
button {
  background-color: #007bff;
  color: #fff;
  border: none;
  padding: 10px 20px;
  cursor: pointer;
}

button:hover {
  background-color: #0056b3;
}
