css:
@-moz-document url-prefix("https://suite8.emarsys.net/bootstrap.php") {

.e-steps {
  transform: scale(0.6) !important;
  transform-origin: top right !important;
  width: auto !important;
  position: absolute !important;
  top: 7px !important;
  left: auto;
  right: 300px !important;
  z-index: 99999999 !important;
  margin-left: 0 !important;
  padding: 0 !important;
}

	.e-layout-with_steps {
    padding-bottom:24px
  }

.e-layout.e-layout-without_navigation.e-layout-fullheight.e-layout-with_steps {
  padding-top: 0 !important;
  padding-bottom:12px !important;
  padding-right:12px !important;
  padding-left:12px !important;
}

cb-header.e-layout__header {
  margin-bottom:12px !important;
  padding-bottom: 6px !important;
}

h1.e-layout__title {
  font-size: 24px !important;
}

main.e-layout__content .e-layout__section.e-contentblocks-navigation_section.e-layout__section-sidebar_with_verticalnav {
  margin-right:6px !important;
}

main.e-layout__content section.e-layout__section.e-contentblocks-preview_section {
  margin-left:6px !important;
}

cb-test-email-button button.e-btn {
  position: fixed !important;
  top: 7px !important;
  z-index: 9999999 !important;
  right: 12px !important;
  color: #feffff !important;
  background: #0070f2 !important;
}

cb-save-button {
  display: none !important;
}

.e-layout__actions {
  right: 97px !important;
  position: absolute !important;
}

.e-contentblocks-preview_section .e-contentblocks-preview { 
  top: 0 !important;
  height: 100% !important;
}

.e-shellbar {
  display:none !important;
}

cb-app .e-layout__application {
  top: 7px !important;
  height: calc(100vh - 7px) !important;
  }
  .e-navigation {
  top: 7px !important
}


}
