css:
@-moz-document domain('www.webnovel.com'){
  /*[[math]]*/
  
  :root{
--th-bg1: /*[[th-bg1]]*/;
--th-bg2: /*[[th-bg2]]*/;
--th-border: /*[[th-border]]*/;
--th-text1: /*[[th-text1]]*/;
--th-text2: /*[[th-text2]]*/;
--th-anchor: /*[[th-anchor]]*/;
  }

}
@-moz-document regexp('http(?:s|)://(?:www.)webnovel.com/book/[\\s\\S]+?/[\\s\\S]*'){
	/* | HTML | */
	._color3{
	  background-color: var(--th-bg2);
	}
	/* | Chapter Header and Page | */
	._color3 .cha-header,
	._color3 .cha-page{
  	border-color: var(--th-border);
  	background-color: var(--th-bg1);
  }
	._color3 i._badge,
	._color3 i._dot{
  	display: flex;
  	align-items: center;
  	border-color: var(--th-bg1)
	}

	/* | Chapter Content | */
	._color3 .cha-content{
 		color: var(--th-text1);
	}
	._color3 .cha-content._lock:after{
  	background: linear-gradient(180deg,transparent,var(--th-bg1));
	}
	/* | Chapter Fly | */
	._color3 .cha-fly{
  	background-color: var(--th-bg1);
	}
	/* | Chapter Tools Modal | */
	._color3 .cha-modal-hd,
	._color3 .cha-modal{
  	background-color: var(--th-bg1);
  	border-color: var(--th-border);
	}
	._color3 #modalCatalog .cha-modal-hd{
  	-webkit-box-shadow: 0 8px 10px var(--th-bg1);
  	box-shadow: 0 8px 10px var(--th-bg1);
	}
	._color3 .m-pagination{
  	background-color: var(--th-border);
	}
	._color3 .g_mod_hd{
  	color: var(--th-text2);
	}
	._color3 .theme-set-font strong,
	._color3 .theme-set-upDown,
	._color3 .ui-select-button{
  	border-color: var(--th-border);
	}
	/* | Chapter Comments | */
	._color3 .g_mod{
  	background-color: var(--th-bg1);
	}
	._color3 .rich_modal_body .modal_quote,
	._color3 .m-input{
  	background-color: var(--th-bg2);
  	border-color: var(--th-border);
	}
	._color3 .rich_modal_body .area_box .rich_editor{
  	background-color: var(--th-border);
  	color: var(--th-text1);
	}
	._color3 .g_chk span{
  	background-color: var(--th-bg2);
	}
	._color3 .g_chk span i{
  	background-color: var(--th-bg1);
	}
	/* | Open Paragraph Comment | */
	._color3 .para-comment-num{
  	background-color: var(--th-border);
  	color: var(--th-text1);
	}
	._color3 .operate-bar{
  	background-color: var(--th-border);
	}
  ._color3 .operate-bar a{
  	color: var(--th-text1);
	}
  ._color3 .operate-bar:after,
  ._color3 .operate-bar._reverse:after{
    border-top-color: var(--th-border);
		border-bottom-color: var(--th-border);
  }

  /* | Details | */
	._color3 .g_mod:before{
 		border-color: var(--th-anchor);
	}
	._color3 a,
	._color3 .theme-set-color ._on,
	._color3 .theme-set-upDown i:hover,
	._color3 .m-pagination a._on{
  	color: var(--th-anchor);
	}
	._color3 .g_chk span:before,
	._color3 .cha-tools a._on,
	._color3 .cha-tools a:hover,
	._color3 .m-pagination a:before{
  	background-color: var(--th-anchor);
	}
	._color3 .theme-set-font input:checked+strong,
	._color3 .theme-set-color ._dark input:checked+strong{
  	color: var(--th-anchor);
  	border-color: var(--th-anchor);
	}
	._color3 .theme-set-color ._dark strong{
  	background-color: var(--th-bg1);
	}
	/*	| QRCODE |	*/
	._color3 .qrcode_size canvas{
  	border: 16px solid white;
	}
}
