css:
@-moz-document url(https://macroscop.com/podderzhka/kalkulator), url(http://testcalculator.macroscop.com), url(http://dev.macroscop.com/podderzhka/kalkulator) {
	.form__group, .form__label, .header__top-menu, .header__main-menu, .calculator__title, .calculator__section {
		margin-bottom:0!important
	}
	.sub-navigation, .module-item__dropdown-content app-analytic-module-icon, .form__group--low-margin label, .form__note, .result__item-icon svg, .header-sticky, .module-settings__table-header, .form__range {
		display:none!important
	}
	.divider, .equipment-settings, .calculator__section-new {
		margin-bottom:5px!important
	}
	.select-dropdown__dropdown-item, .radio-toggle__item, .radio-toggle__label {
		min-height:inherit!important
	}
	.form__input, .select-dropdown__selected {
		padding:3px 5px!important;
		border-radius:0!important;
	}
	.select-dropdown, .select-dropdown__selected {
		height:auto!important
	}
	.select-dropdown__selected, .form__submit button, .radio-toggle, .radio-toggle__label, .divider__button, .calculator__content button, .button--small, .ng-binding {
		border-radius:0!important
	}
	.select-dropdown__selected::after {
		top:11px!important
	}
	.radio-toggle__label {
		padding:3px 40px!important;
	}
	.divider__button, .calculator__content button, .form__submit button, .button--small, .select-dropdown__dropdown-item input {
		padding:5px 15px!important
	}
	.module-item__btn-remove {
		margin-top:0!important;
		margin-bottom:5px!important;
	}
	.module-item .select-dropdown__selected::after, .module-item .select-dropdown__selected-title::after {
		right:5px!important
	}
	.calculator {
		overflow:inherit!important;
		padding:0 20px 10px!important
	}
	.header__top-menu-right {
		padding:14px 0!important
	}
	.header__top-menu, .module-settings__table-column {
		padding:0!important
	}
	.select-dropdown__dropdown {
		min-width:inherit!important;
	}
	.select-dropdown__dropdown-item input {
		border:none!important
	}
	.select-dropdown__dropdown-item {
		padding: 0 12px!important;
	}
	.select-dropdown__dropdown-item:hover, .form__submit {
		margin: 0 !important;
	}
	.module-item__dropdown-content-text {
		margin-left: 0!important;
	}
	.form__group-title-content {
		padding-top: 21px!important;
		text-align:right!important;
	}
	.result__item-content {
		padding: 5px 20px!important;
	}
	.result__item-icon {
		margin: 0!important;
	}
	.result__item-table-row {
		border-bottom: 1px solid!important;
		border-color: lightgray!important;
	}
	.result__item-table-row:last-child {
		border-bottom: 0!important;
	}
	.result__item-table-cell {
		padding: 5px 5px!important;
	}
	.result__item-icon {
		height: 30px!important;
	}
	app-select-dropdown {
		width: 100%!important;
	}
	.select-dropdown--style-bold .select-dropdown__selected-title, .select-dropdown__selected-title {
		text-decoration: none!important;
		font-weight: inherit!important;
	}
	app-analytic-module-settings .form__flex-group-wrapper {
		margin-bottom: 0!important;
	}
	.form__flex-group-wrapper-title {
		min-width: 174px!important;
		text-align: right!important;
	}
	.calculator__section-new .divider__line, .calculator__section .divider__line {
		display: none!important;
	}
	.calculator__section-new .divider__button, .calculator__section .divider .ng-binding {
		margin: auto!important;
	}
	.calculator__section .divider .ng-binding {
	  background-color: #0094d1;
		text-align: center;
		cursor: pointer;
		color: #fff;
		padding: 5px 15px!important;
		white-space: nowrap;
	}
	.calculator__section .divider .ng-binding:hover {
		  background-color: #e84d30;
	}
	.calculator__section .divider .ng-binding a {
	  color:white!important
	}
}
