css:
@-moz-document domain("waz.de"), domain("embedded.tankentanken.de") {
.pub-nrw-waz {
    background: #d3d3d3 !important;
}
.page {
    background: #d3d3d3 !important;
}
.header {
    background: #d3d3d3 !important;
}
.breadcrumb {
    max-width: 100%;
    border: 1px solid red;
    border-radius: 4px;
    margin-right: 5px;
}
.breadcrumb__item {
    background: #eeeeee !important;
    border-radius: 4px;
}
.breadcrumb::after {
    border: none;
}
.block-header__sub {
    background: #eeeeee !important;
    border-radius: 4px;
}
.nav-secondary {
    background: #eeeeee !important;
    border-radius: 4px;
}
.nav-main {
    border-radius: 4px;
    border: none;
}
.content--alternative-background {
    border-radius: 4px;
}
.teaser--img-vertical {
    border-radius: 4px;
}
:root {
    --main-bg-color: #d3d3d3 !important;
}
.teaser__img--article {
    border-radius: 4px;
}
.content-extrawide {
    border-radius: 4px;
    background: #d3d3d3 !important;
}
.teaser {
    border-radius: 4px;
    background: #d3d3d3 !important;
}
.pub-nrw-waz .article .hero-img .hero-img__img {
    border-top: none;
}
.hero-img .hero-img__img img:last-child {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
.hero-img__caption {
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    border-left: 1px solid darkgrey;
    border-right: 1px solid darkgrey;
    border-bottom: 1px solid darkgrey;
}
.article .hero-img {
    background: #d3d3d3 !important;
}
.content--teaser--container {
    background: #d3d3d3 !important;
}
.block-header--banner {
    border-radius: 4px;
}
.teaser-carousel--multi {
    border-bottom: 1px solid darkgrey;
}
.teaser-list__item {
    border: none;
}
div.opulenz .content-with-sidebar .sidebar .content--teaser--container {
    background: #191919 !important;
    border: none;
}
.teaser-list__item {
    background: #191919 !important;
    border: none;
}
div.opulenz .content-with-sidebar .sidebar .teaser-list article {
    background: #191919 !important;
    border: none;
}
.loading {
    border-radius: 4px;
}
.loaded {
    border-radius: 4px;
}
.av-main__col {
    background: #d3d3d3 !important;
}
.nav-main__teaser-container {
    background: #d3d3d3 !important;
    box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24), 0 17px 30px 0 rgba(0, 0, 0, 0.19);
    border-radius: 4px;
}
.nav-main .nav-main__flyout {
    background: #d3d3d3 !important;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border: none;
}
.nav-main .nav-main__flyout .nav-main__list--level2 {
    border-top-left-radius: 4px;
    border-bottom-left-radius: 4px;
}
}
