css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com") {
  /*Ad removal*/
  .ad,
  .ad *,
  .img_ad,
  .ad-annotations,
  #google_image_div,
  #aw0,
  #AdvertisingLeaderboard {
    display: none !important;
  }

  /*Header and Dialog styles*/
  #header {
    background-color: #020000;
  }

  .dialog-container .dialog-header {
    background-color: #020000;
    color: #020000;
  }

  /*Navigation and Chat styles*/
  .navigation .notification-icon,
  .rbx-highlight,
  #chat-header {
    background: #020000;
  }

  /*Forum and Upgrade Button styles*/
  .forum-table-header,
  body .forum-table-header th,
  body .forum-table-footer,
  #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
  #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th,
  .rbx-upgrade-now #upgrade-now-button,
  .rbx-btn-secondary-xs.btn-more,
  #AjaxCommentsContainer > div > div.comment-form > form > button,
  .rbx-vip-server-create,
  .btn-secondary-xs {
    background-color: #020000 !important;
    border-color: #020000;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
  .rbx-btn-secondary-xs.btn-more:hover,
  #AjaxCommentsContainer > div > div.comment-form > form > button:hover,
  .rbx-vip-server-create:hover,
  .btn-secondary-xs:hover {
    background: #020000 !important;
    border-color: #020000;
  }

  /*Miscellaneous styles*/
  #TopAbpContainer,
  .abp-spacer,
  #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
  #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
  #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
  #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
  .sponsored-game,
  .alert-info,
  #mCSB_1_container > ul > li.font-bold.small,
  #mCSB_1_container > ul > li.rbx-nav-sponsor,
  .rbx-btn-primary-lg {
    display: none !important;
  }

  /*Styling links and search bar*/
  .gotham-font .btn-to-link,
  .gotham-font .refresh-link,
  .gotham-font .see-all-link,
  .gotham-font .touch .btn-to-link,
  .gotham-font .touch .refresh-link,
  .gotham-font .touch .see-all-link {
    color: #020000;
  }

  #navbar-search-input,
  #navbar-search-btn {
    border: 1px solid #020000;
  }

  /*Background and Container styles*/
  html,
  body {
    background-image: url("https://cdn.discordapp.com/attachments/857968049567432714/1132483292281708625/pexels-jovydas-pinkevicius-3113541.jpg") !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .content {
    background: none !important;
  }

  .container-footer {
    position: static;
  }

  /*Party header and Chat styles*/
  #party_none_title,
  div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #020000, #020000);
  }

  /*Adjusting column styles*/
  #ctl00_cphRoblox_RightColumn,
  #Body > table > tbody > tr > td:nth-child(3),
  #Body > table > tbody > tr > td:nth-child(4),
  #GamesPageLeftColumn {
    display: none;
  }

  #GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
  }

  /*Navigation background color*/
  #navigation {
    background-color: #020000;
  }

  /*Notification styles*/
  .notification-red,
  .notification-blue {
    background-color: #020000;
    padding: 1px 6px;
    min-width: 18px;
  }

  /*Button styles*/
  .rbx-btn-primary-lg:hover,
  .rbx-btn-primary-lg:focus {
    background-color: rgba(200, 160, 248, 0.5);
  }

  /*Avatar background*/
  .avatar-back {
    background-image: url("https://cdn.discordapp.com/attachments/857968049567432714/1132483292281708625/pexels-jovydas-pinkevicius-3113541.jpg");
  }
}
