/* 第二部分规则 */

@-moz-document domain("www.baidu.com"),
domain("www1.baidu.com"),
domain("www.m.baidu.com") {

    /* 百度-卡片-简 */

    /*这些是每个搜索结果的样式*/
    .c-container {
        padding: 15px;
        margin-top: 2em;
        border-radius: 8px;
        box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, .15);
        background-color: #9c9c9c12;
        transition: 0.3s;
        transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
    }
    .c-container:hover {
        background-color: #9c9c9c4f;
        box-shadow: 0px 0px 0px 2px rgba(120, 143, 194, .65);
    }
    .c-border {
        background: #fffffff0;
    }
    A {
        text-decoration: none;
    }

    /*知道搜索界面*/
    .dl {
        background-color: #9c9c9c12;
        margin: 10px;
        padding: 10px;
        border-radius: 5px;
        box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, .15);
        transition: 0.3s;
        transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
    }
    .dl:hover {
        background-color: #9c9c9c4f;
        box-shadow: 0px 0px 0px 2px rgba(120, 143, 194, .65);
    }
    .list-wraper .dt {
        font-size: 13px;
    }

    /*隐藏掉右边的广告和相关搜索 */

    /* 相关搜索美化 */
    .cr-content {
        padding: 15px;
        margin-top: 2em;
        border-radius: 8px;
        color: #000000fa;
        /* 	background-color: #2cece412; */
        transition: 0.3s;
        transition-timing-function: cubic-bezier(0.15, 0.87, 0.36, 0.93);
        box-shadow: inset 2px 3px 7px 5px #11111112;
        /* 该隐藏功能的开关 */
        /* 		visibility: hidden; */
    }
    /* 卡片美化 */
    .cr-content a {
        color: #000;
    }
    #content_right {
        border-left: 1px solid #fff0;
    }
    A {
        color: black;
        text-shadow: 0 0 10px rgba(0, 0, 0, 0.15);
    }
    /* 搜索结果标题搜索词指示 */
    /*  a em { */
    /*     text-decoration: none; */
    /*     background-color: #00ffee33; */
    /*     border-color: #04040424 !important; */
    /*     border-radius: 6px; */
    /*     background-size: cover; */
    /*     color: #046ca9; */
    /* } */
}



/* 第三部分规则 */

@-moz-document domain("www.baidu.com"),
domain("www1.baidu.com"),
domain("www.m.baidu.com") {

    /* 百度多栏卡片搜索 */
    body {
        overflow-x: hidden;
        min-width: 600px;
        font-family: "微软雅黑";
    }
    .wrapper_l #content_left {
        padding-left: 20px;
    }
    .wrapper_l .s_form {
        width: 792px !important;
        margin: 0 auto !important;
        transform: translatex(-10px);
    }
    .wrapper_l .s_form_wrapper {
        display: flex;
    }
    .wrapper_l .s_ipt_wr {
        width: 560px;
    }
    .s_tab,
    .nums,
    .search_tool_conter,
    #rs,
    .to_tieba,
    .to_zhidao_bottom {
        margin: 0 auto !important;
    }
    .headBlock {
        margin-left: 0;
    }
    .headBlock > div {
        margin: auto;
    }
    .head_nums_cont_inner > div {
        width: 600px;
    }
    .new-pmd .c-border {
        box-shadow: none;
        padding-left: 30px;
    }
    .op-musicsong {
        margin-top: 15px;
    }
    .result > .result, .result-op > .result {
        margin-bottom: 0 !important;
        margin-left: 10px;
    }
    #page,
    #s_tab {
        padding-left: 0px !important;
        text-align: center !important;
    }
    #container,
    #container.sam_newgrid {
        width: 100%;
        margin-left: 0;
        padding: 0 40px;
        box-sizing: border-box;
    }
    #content_right {
        display: none;
    }
    #container.sam_newgrid #content_left {
        width: 100%;
    }
    #content_left {
        padding: 0;
        width: 100%;
        padding-left: 0;
        display: block;
        counter-reset: results;
        break-inside: avoid;
        margin-top: 15px;
        column-gap: 20px;
    }
    #tsn_inner > div {
        margin: auto;
    }
    .rrecom-container,
    .xpath-log:not(.c-container) {
        transform: scale(0);
    }
    @media (min-width: 1201px) and (max-width: 1700px) {
        #content_left {
            column-count: 2;
            -moz-column-count: 2;
            -webkit-column-count: 2;
        }
    }
    @media (min-width: 1701px) and (max-width: 2200px) {
        #content_left {
            column-count: 3;
            -moz-column-count: 3;
            -webkit-column-count: 3;
        }
    }
    @media (min-width: 2201px) {
        #content_left {
            column-count: 4;
            -moz-column-count: 4;
            -webkit-column-count: 4;
        }
    }
    #content_left > *:not([class^="result"]) {
        position: absolute;
        margin-top: -1em;
        transform: scale(0);
    }
    #content_left > *[class^="result"] {
        display: inline-block;
        width: 100%;
        padding: 18px 15px;
        margin-bottom: 20px !important;
        word-break: normal;
        word-wrap: normal;
        position: relative;
        z-index: 1;
        box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.3);
        border-radius: 5px;
        background-color: white;
        overflow: hidden;
        transform: translateZ(0);
        box-sizing: border-box;
    }
    #content_left > *[class^="result"]:before {
        counter-increment: results;
        content: counter(results);
        z-index: -10;
        color: #eee;
        font-size: 4.5em;
        position: absolute;
        text-align: right;
        width: 96%;
        line-height: 1em;
        top: 6px;
    }
    #content_left > *[class^="result"] .sitelink {
        word-wrap: break-word;
    }
    #content_left .result-op > div {
        width: 100%;
        box-sizing: border-box;
    }
    .c-border {
        border: 0;
        box-shadow: none;
    }
    .to_tieba {
        display: none;
    }
    .hint_common_restop {
        display: none;
    }
    #rs {
        width: 100%;
        text-align: center;
        padding: 0;
        margin: 0;
        margin-top: 10px;
    }
    #rs .tt,
    #rs table,
    #rs tr,
    #rs td,
    #rs th {
        display: inline;
        padding: 0 5px;
    }
    #page {
        text-align: center;
        padding: 0;
    }
    #page .fk {
        display: none;
    }
    #page > a,
    #page > strong {
        height: auto;
    }
    #foot {
        text-align: center;
    }
    #foot #help {
        float: none !important;
        padding: 0 !important;
    }
    .c-span6 {
        width: 25% !important;
        margin-right: 0 !important;
        padding-left: 3px;
        padding-right: 3px;
        box-sizing: border-box;
    }
    .c-span18 {
        width: 75% !important;
        padding-left: 10px;
        box-sizing: border-box;
    }

    /* 百度图片 */
    .op-img-address-divide-low {
        height: auto;
        display: flex;
    }
    .op-img-address-link-type {
        float: none;
    }

    /* 百度经验 */
    .c-span6.op_jingyan_list {
        margin-right: 0;
    }
    .op_jingyan_list > a {
        display: block;
        width: 100%;
    }
    .op_jingyan_list > a .c-img6 {
        width: 100%;
        height: auto;
    }
    .op_jingyan_list .op_jingyan_index {
        left: 5px;
        top: 67px;
    }

    /* 百度贴吧 */
    .op-tieba-general-main-col {
        width: 70% !important;
    }

    .sitelink_summary {
        width: 46%;
    }
    #wrapper_wrapper {
        margin: 0 !important;
    }

    .result-molecule #rs_new {
        width: 100%;
        margin-top: 0;
    }

    .result-molecule #rs_new > table {
        width: 100%;
    }

    .result-molecule #rs_new > table tbody {
        display: flex;
        flex-wrap: wrap;
    }

    .result-molecule #rs_new > table tbody > tr {
        padding-right: 16px;
    }

}
