/* ==UserStyle==
@name         Pi Dark Theme for Pi.ai by Guile93
@version      20230724.18.07
@namespace    userstyles.world/user/guile93
@description  A simple Dark Theme for Pi IA
@author       guile93
@license      No License
==/UserStyle== */

@-moz-document url-prefix("https://pi.ai/talk") {
html {
    background-color: #404040
}
.text-brand-green-600{
    --tw-text-opacity:1;
    color:rgb(238 238 238/var(--tw-text-opacity))
}
.text-brand-green-650{
    --tw-text-opacity:1;
    color:rgb(218 218 218/var(--tw-text-opacity))
}
.text-brand-green-700{
    --tw-text-opacity:1;
    color:rgb(151 151 151/var(--tw-text-opacity))
}

.bg-cream-200,.bg-cream-500,.bg-cream-550,.bg-cream-650,.bg-primary{
    --tw-bg-opacity:1;
    background-color:rgb(40 40 40/var(--tw-bg-opacity))
}
}
