css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com") {

 .vertigo2 #Userpic {
    width: 134px !important ;
    background: #3e5a8b !important;
    border-right: solid 1px #000000 !important;
 }

 .vertigo2 #defaultpic {
    margin: 0 auto !important;
 }

 .vertigo2 #NoUserpic {
    width: 100px !important;
    margin: 0 auto !important;
 }

 .vertigo2 #NoUserpic span {
    margin: 0 17px !important;
 }

 .vertigo2 #welcome, .vertigo2 #NavMenu {
    padding-left: 45px !important;
 }

 body.vertigo2 {
    background: url("http://pics.livejournal.com/ljskins_images/pic/000058r2.png") repeat-y 0 0 !important;
 }

 .vertigo2 #nav-vertical {
    background: #3e5a8b !important;
    color: #999999 !important;
 }

 .vertigo2 #nav-vertical li a {
    color: #c5dff9 !important;
 }

 .vertigo2 #nav-vertical li li a {
    color: #ffffff !important;
    text-decoration: underline !important;
 }

 .vertigo2 #nav-vertical li li:before {
    content: "Â» " !important;
    color: #eeeeee !important;
 }

 .vertigo2 #nav-vertical ul li ul li {
    background: none !important;
 }

 .vertigo2 #nav-vertical ul ul {
    margin-left: -7px !important;
 }

 .vertigo2 #nav-vertical ul li ul li.disabled:before {
    content: "" !important;
 }

 .vertigo2 #nav-vertical ul li ul li.disabled {
    padding-left: 23px !important;
    background: url(http://www.livejournal.com/stc/horizon/upgrade-paid-icon-small.gif) no-repeat 5px 50% !important;
 }

}
