css:
    /*:::::::::::::::::::::::::::::::::::::::Do Not Remove This Line::::::::::::::::::::::::::::::::::::::: */

    @namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

    #TabsToolbar
    {
    -moz-box-ordinal-group: 0 !important;
    }

    #navigator-toolbox toolbar
    {
    -moz-box-ordinal-group: 200 !important;
    }

    .tab-drop-indicator
    {
    margin-top: 0px !important;
    }

    #navigator-toolbox
    {
    -moz-appearance: none !important;
    /*background: -moz-linear-gradient(top, rgba(255,255,255,0), rgba(255,255,255,0), rgba(255,255,255,0), rgba(255,255,255,0), rgba(255,255,255,0), rgba(249,250,250,0), rgba(239,242,242,0), rgba(229,234,233,0), rgba(219,226,227,0), rgba(208,216,217,0)) !important;*/
    background: -moz-linear-gradient(top, rgba(255,255,255,0)15%, rgba(255,255,255,0), rgba(255,255,255,0), rgba(249,250,250,0), rgba(239,242,242,0), rgba(229,234,233,0), rgba(219,226,227,0), rgba(208,0,0,0)) !important;
    border-style: none !important;
    -moz-box-shadow: inset threedshadow 0px -0px !important;
    padding-bottom: 10px !important;
    }

    #navigator-toolbox #nav-bar
    {
    padding-top: 10px !important;
    padding-bottom: 9px !important;
    }

    #TabsToolbar
    {
    -moz-box-shadow: ThreeDShadow 0 -0px inset !important;
    }

    #TabsToolbar .tabbrowser-tab[selected=true]
    {
    background:  -moz-linear-gradient(top, rgba(255,255,255,0), rgba(255,255,255,.9)) !important;
       color: black !important;
text-shadow: 0 0 .6em white, 0 0 .6em white, 0 0 .6em white !important;   
}

    #TabsToolbar .tabbrowser-tab
    {
    background-color: rgba(255,255,255,0)!important;
    margin-left: 2.5px !important;
    margin-right: 2px !important;
    -moz-border-radius-topleft: 12px !important;
    -moz-border-radius-topright: 0px !important;
    color: black !important;
    text-shadow: 0 0 .3em white, 0 0 1em white, 0 0 .3em white !important;
    }

    #TabsToolbar .tabs-newtab-button,
    #TabsToolbar .tabs-alltabs-button,
    #TabsToolbar .scrollbutton-up,
    #TabsToolbar .scrollbutton-down
    {
    background: rgba(255,255,255,0)!important;
    }

#browser-bottombox
{
-moz-appearance: none !important;
background: -moz-linear-gradient(top, rgba(0,0,0,0), rgba(0,0,0,0), rgba(0,0,242,0), rgba(0,0,0,0), rgba(0,0,0,0), rgba(0,0,0,0)) !important;
border-top: none !important;
border-left: 0px solid threedshadow !important;
border-right: 0px solid threedshadow !important;
border-bottom: 0px solid threedshadow !important;
-moz-box-shadow: inset threedshadow 0px 0px !important;
}

#status-bar
{
-moz-appearance: none !important;
background: transparent !important;
border: 0 !important;
font-weight: bold;
text-shadow: 0 0 .6em white, 0 0 .6em white, 0 0 .6em white !important;
}

statusbarpanel 
{
-moz-appearance: none !important;
border-left: 0px !important;
border-right: 0px !important;
border-bottom: 0px !important;
border-top: 0px !important;
margin-bottom: 4px !important;
margin-top: 6px !important;
margin-right: 2px!important;
}



    #HeadlinesTicker-Icon
    {
    margin-right: 8px !important;
    }
