@-moz-document domain(scratch.mit.edu) {

@keyframes grad1 {
  0%   {background-color:#1d1d1d; left:0px; top:0px;}
  50%  {background-color:#2c2c2c; left:0px; top:0px;}
  100%  {background-color:#1d1d1d; left:0px; top:0px;}
}

#view {
    background-image: url(https://i.imgur.com/yonNvPh.png)
        !important;
    position: sticky!important;
}

.box-content, .activity, .box-header,.news,.box{
