}
#donor {
    color: #575e75;
    background-color: #2c2c2c;
}
.stage_green-flag-overlay_gNXnv{
    background: url(https://i.imgur.com/34Z4R90.png) no-repeat;
    background-position: center center;
    background-size: 40px;
    padding-left: 60px;
    border: 3px solid hsl(0deg 0% 0%);
}

/* EDITOR */

.monitor_default-monitor_2vCcZ{
    color: rgb(0 0 0) !important;
}
.gui_body-wrapper_-N0sA {
    background-color: rgb(45 53 64);
}
.input_input-form_l9eYg,.sprite-info_sprite-input_17wjb{
    text-align: center;
    background-color: rgb(45 53 64)!important;
    cursor: vertical-text!important;
    border-color: -internal-light-dark(rgb(118, 118, 118), rgb(195, 195, 195));
}
.blocklySvg,
.stage-selector_stage-selector_3oWOr,
.sprite-selector_sprite-selector_2KgCX{
    animation-name: grad1;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}
.menu-bar_menu-bar_JcuHF,.stage-header_stage-button_hkl9B {
    background-color: #1d1d1d;
}
.stage-selector_header_2GVr1,.sprite-info_sprite-info_3EyZh {
    background-color: #2c2c2c;
}
@keyframes grad2{
    0% {
        -webkit-filter:invert(50%);
        -filter: sepia(85%);
        -filter:saturate(1553%);
        -filter:hue-rotate(157deg);
        -filter:brightness(89%);
        -filter:contrast(101%);
    }50% {
        -webkit-filter:invert(100%);
        -filter: sepia(85%);
        -filter:saturate(1553%);
        -filter:hue-rotate(187deg);
        -filter:brightness(89%);
        -filter:contrast(101%);
    }100% {
        -webkit-filter:invert(50%);
        -filter: sepia(85%);
        -filter:saturate(173%);
        -filter:hue-rotate(117deg);
        -filter:brightness(89%);
        -filter:contrast(101%);
    }
}

@keyframes grad3{
    0% {-webkit-filter:brightness(100%);}
    50% {-webkit-filter:brightness(70%);}
    100% {-webkit-filter:brightness(100%);}
}

.menu-bar_scratch-logo_2uReV,.menu-bar_clickable_1g3uo{
    
    background: url(https://i.imgur.com/zX1UwPX.png) no-repeat;
    background-position: center center;
    background-size: 70px;
    padding-bottom: 100px;
    
    
    animation-name: grad3;
    animation-duration: 2s;
    animation-iteration-count: infinite;
    
    pointer-events: none; 
    cursor: default; 
}

/* TOOLBAR */

.gui_tab_27Unf.gui_is-selected_sHAiu,
.gui_tab_27Unf,
.blocklyToolboxDiv,
.scratchCategoryMenuRow,
