}
@keyframes AnimCatergoryText{
    0% {color: rgb(0,0,0);}
    50% {color: rgb(100,100,100);}
    100% {color: rgb(0,0,0);}
}
.scratchCategoryMenuItemLabel{
    animation-name: AnimCatergoryText;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.blocklyFlyoutBackground{
    fill: #0000004a;
}

/* BLOCKS */

@keyframes AnimBlock{
    0% {fill: rgb(255,0,0);}
    7% {fill: rgb(200,50,0);}
    15% {fill: rgb(150,100,0);}
    21% {fill: rgb(100,150,0);}
    27% {fill: rgb(50,200,0);}
    33% {fill: rgb(0,255,0);}
    40% {fill: rgb(0,200,50);}
    47% {fill: rgb(0,150,100);}
    53% {fill: rgb(0,100,150);}
    59% {fill: rgb(0,50,200);}
    65% {fill: rgb(0,0,255);}
    72% {fill: rgb(50,50,200);}
    78% {fill: rgb(100,50,150);}
    86% {fill: rgb(150,50,100);}
    93% {fill: rgb(200,50,50);}
    100% {fill: rgb(255,0,0);}
}


@keyframes AnimGrey{
    0% {fill: #1d1d1d;}
    50% {fill: #2c2c2c;}
    100% {fill: #1d1d1d;}
}
@keyframes AnimGrey2{
    0% {fill: #2b2b2b;}
    50% {fill: rgb(53 53 53);}
    100% {fill: #2b2b2b;}
}
@keyframes AnimGrey3{
    50% {stroke: rgb(255,255,255);}
}

/* BLOCKS */

.blocklyPath:hover,.blocklyBlockBackground:hover{
    animation-name: AnimGrey3;
    animation-duration: .5s;
    animation-iteration-count: infinite;
}
.blocklyPath,.blocklyBlockBackground{
