    animation-duration: 0.5s;
    animation-iteration-count: infinite;
}
.categorySelected{
    background-color: #1d1d1d !important;
}
@keyframes AnimBlockyText{
    50% {fill: rgb(150,150,150);font-size: 12.3pt;}
}

.blocklyNonEditableText>text, .blocklyEditableText>text {
    fill: rgb(255,255,255);
    /*
    animation-name: AnimBlockyText;
    animation-duration: 1s;
    animation-iteration-count: infinite;
    */
}
.blocklyNonEditableText>text:hover, .blocklyEditableText>text:hover {
    fill: rgb(255,255,255);
    animation-play-state: paused;
    animation-name: none;
}
@keyframes AnimGrey4{
    50% {fill: rgb(45 53 64);}
}
.blocklyPath[data-argument-type='boolean']{
    stroke: rgb(0 0 0);
    fill: #2c2c2c;
    animation-name: AnimGrey4;
}
@keyframes AnimBackground{
    0% {width: 0%;}
    50% {width: 100%;}
    100% {width: 0%;}
}
.blocklyMainBackground{
