#IL_INSEARCH,
.IL_BASE
{display: none;}

#about-right iframe,#about-right object,#about-right img {display:none}
#hi5-common-header-banner, .hi5-common-header-banner-ad {display:none!important;} 
#gamesModule {display:none;}
iframe {visibility: hidden}
#content-left iframe
{visibility: visible !important}

/*

 **************************************************

 * --- Created April 27 2009 --- By Barbie Foster *

 * Barbiegirl  http://userstyles.org/users/13023  *

 * TITLE: BUTTONS - Glossy Black (input)          *

 * http://userstyles.org/styles/17353             *

 **************************************************

*/



/* UPDATE#1:APRIL 29 2009:#1 REMOVED BLUE TINT FROM BUTTONS. #change border color so it can be seen on black background webpages */

/*UPDATE FEB 7 2010: WILL NOT AFFECT YOUTUBE*/





button:not(.master-sprite):not([anonid="twisty"]),

input[type="submit"] 	

{

-moz-appearance: none !important;

width: auto !important;

height: auto !important;



background-color: #555555!important;

color: #ccc !important;

font-weight: normal !important;		

border: 1px solid #000000 !important;

		

outline: 3px solid #333333!important;

-moz-outline-offset: -1px !important;

-moz-outline-radius: 6px!important;



background-image:url("data:image/png;base64,") !important;



}





button:not(.master-sprite):not([anonid="twisty"]):hover,

input[type="submit"]:hover 	

{opacity: .8 !important;}



button:not(.master-sprite):not([anonid="twisty"]):active /* save,cancel */

/*input[type="submit"]:active */	

{

-moz-appearance: none !important;

background: #000000 !important;

font-weight: normal !important;

font-size: 11px !important;

color: #777777 !important;

border: 1px solid #000000 !important;		

outline: 3px solid #222222 !important;

-moz-outline-offset: -1px !important;

-moz-outline-radius: 6px!important;

/*background-image:none!important;*/

}





input[type="submit"]:active  /*google*/	

{

-moz-appearance: none !important;

background: #000000 !important;

font-weight: bold !important;

font-size: 12px !important;

color: #777777 !important;

border: 1px solid #000000 !important;		

outline: 3px solid #222222 !important;

-moz-outline-offset: -1px !important;

-moz-outline-radius: 6px!important;



}



/*////////////////////////////////////////////////////////*/

/*////////////////////////////////////////////////////////*/

/*////////////////////////////////////////////////////////*/

/*************************************************************/



/* PREVENTS THE NAVIGATOR THROBBER from being affected */

#navigator-throbber, #throbber-box 

{

-moz-appearance: none !important;

background: transparent !important;

outline: 3px solid transparent !important;

/*-moz-outline-offset: -1px !important;*/

/*-moz-outline-radius: 6px!important;*/

}

/*************************************************************/



/* PREVENTS THE SEARCH ENGINE BUTTON from being affected */

#searchbar-engine-button, .searchbar-engine-button,

 .searchbar-engine-button[type="menu"],searchbar-textbox

{-moz-appearance: none !important;

background: transparent !important;

outline: 3px solid transparent !important;

border:none!important;}



/* PREVENTS SEARCH ENGINE BUTTON from being affected ON HOVER */

#searchbar-engine-button, .searchbar-engine-button:hover

{-moz-appearance: none !important;

background: transparent !important;

outline: 3px solid transparent !important;}



/*********************************************************/



/* TO PREVENT INTERFERENCE on hover in searchboxes */

.textbox-input-closebutton:hover 

{-moz-appearance:none!important;

outline: 3px solid transparent !important;  }



/*********************************************************/



/* TO PREVENT interference with RSS feed button */

#feed-button

{-moz-appearance: none !important;

background: transparent !important;

outline: 3px solid transparent !important;}



/**********************************************/

/*PREVENT INTERFERENCE WITH DICTIONARY FIREFOX ADDON - STATUSBAR ICON*/

#dictionaryDictsearchbutton

{-moz-appearance: none !important;

background: transparent !important;

outline: 3px solid transparent !important;}

/*****************************************************/



/*TO PREVENT BUTTON INTERFERENCE WITH THE WEBSITE youtube.com*/

/*BELOW YOU CAN ADD MORE DOMAIN WEBSITES THAT ARE INTERFERED*/

@-moz-document domain("youtube.com") {

button

{ -moz-appearance:none !important; }



button:hover,

button:active

{background-color:transparent!important;

-moz-appearance:none!important;

border:none!important; }



}

/*END*/
