css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {

#timeline .hentry.share,
#logo, #header, #footer, #timeline_heading, #side_ad_base, #trends, #home_tab, #me_tweets,
#custom_search, #following, #rssfeed, #update_notifications, #next_steps, #recommended_users,
.stats, .content-bubble-arrow, .promotion, span.lock-icon, img.side_thumb {
	display:none !important;
	background-image:none !important;
}

.meta, .meta *, .entry-meta, .entry-meta * {
	display:inline !important;
}

.entry-meta {
	margin-left:1em !important;
}

#new_results_notification {
	width:14em !important;
}

#content, #container {
	width:95% !important;
}

#content {
	margin:0 !important;
	padding:0 !important;
}

li {
	border-color:transparent !important;
}

span.status-body {
	width:90% !important;
}

.actions {
	width:70px !important;
}

.actions a {
	display:inline !important;
	float:right !important;
}

a.screen-name {
	margin-bottom:3px !important;
	display:block !important;
}

#status_update_box h3, #currently {
	visibility:hidden !important;
	margin:0 !important;
	padding:0 !important;
}

img.photo {
	width:30px !important;
	height:30px !important;
	margin:5px 0 0 0 !important;
	padding:0 !important;
}

.entry-content {
	height:1em !important;
	margin-bottom:0 !important;
	padding:0 !important;
}

}
