css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.ru") {
/* from /unyl/ with love */
/* iichan.ru - photon style */
html, body {
	background-color: #EEEEEE !important;
	color: #333333 !important;
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
}
* {
	font-family: inherit !important;
	font-size: inherit !important;
}
a {
	color: #FF6600 !important;
	text-decoration: underline !important;
}
a:hover {
	color: #0066FF !important;
}
.adminbar {
	clear:both !important;
	float:right !important;
	font-size: .8em !important;
	text-align: left !important;
}
.adminbar a {
	font-weight: bold !important;
}
.logo {
	clear:left !important;
	text-align:left !important;
	font-size:2em !important;
	font-weight: bold !important;
	color:#FF6600 !important;
}
div.logo img {
	display: none !important;
}
div.logo br {
	display: none !important;
}
.theader, .passvalid {
	background:#DDDDDD !important;
	text-align:center !important;
	padding:2px !important;
	color:#2266AA !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #CCCCCC !important;
	-moz-border-radius: 5px !important;
}
.postarea {
}
.rules table {
	margin: inherit !important;
}
.rules {
	font-size: inherit !important;
}
.rules table {
	font-size: inherit !important;
	margin: inherit !important;
}
.prohibition {
	color: inherit !important;
}
.prohibition ul, .info ul {
	margin: inherit !important;
	padding: inherit !important;
}
.postblock {
	background:transparent !important;
	color:#002244 !important;
	font-weight:bold !important;
	border: inherit !important;
	padding: inherit !important;
}
.footer {
	text-align:center !important;
	font-size:12px !important;
	font-family:serif !important;
	margin: 2em 0 0 0 !important;
	clear: both;
	padding-top: inherit !important;
}
.dellist {
	font-weight: bold !important;
	text-align:center !important;
}
.delbuttons {
	text-align:center !important;
	padding-bottom:4px !important;
}
.managehead {
	background:#DDDDDD !important;
	color:#002244 !important;
	padding:0px !important;
}
.postlists {
	background:#FFFFFF !important;
	width:100% !important;
	padding:0px !important;
	color:#800000 !important;
}
.row1 {
	background:#DDDDDD !important;
	color:#002244 !important;
}
.row2 {
	background:#CCCCCC !important;
	color:#002244 !important;
}
.unkfunc {
	background:inherit !important;
	color:#789922 !important;
}
.reflink {
	font-size: .8em !important;
	font-weight: bold !important;
}
.filesize {
	text-decoration:none !important;
	color: #666666 !important;
	font-size: .8em !important;
	padding-left: inherit !important;
}
.filetitle {
	background:inherit !important;
	font-size:1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}
.postername {
	color:#004A99 !important;
	font-weight:bold !important;
}
.postertrip {
	color:#FF3300 !important;
}
.oldpost, .notabene {
	color:#CC1105 !important;
	font-weight:bold !important;
}
.omittedposts {
	color:#666666 !important;
	font-size: inherit !important;
	font-family: inherit !important;
	color: inherit !important;
	font-weight: inherit !important;
}
.reply {
	background:#DDDDDD !important;
	border: solid 1px #CCCCCC !important;
	padding: 0 !important;
	margin: 0 !important;
	-moz-border-radius: 5px !important;
	color: inherit !important;
}
blockquote {
	margin: .5em .5em .5em 1em !important;
}

.reply blockquote {
	margin: .5em !important;
}
.doubledash {
	display: none !important;
 	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}
.replytitle {
	font-size: 1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}
.commentpostername {
	color:#004A99 !important;
	font-weight:800 !important;
}
.thumbnailmsg {
	font-size: .6em !important;
	color:#666666 !important;
}
hr {
	border-style: solid none none none !important;
	border-width: 1px !important;
	border-color: #BBBBBB !important;
}
table {
	border-style: none !important;
}
table td {
	border-style: none !important;
}
.nothumb {
	background-color: #FFFFFF !important;
	border-style: dotted !important;
	margin: .3em .5em !important;
}
.abbrev {
	color:#666666 !important;
}
.highlight {
	background:#EEDACB !important;
	color:#333333 !important;
	border: 2px dashed #EE6600 !important;
}
.category {
	font-family: inherit !important;
	font-size: inherit !important;
	margin-bottom: inherit !important;
	background: inherit !important;
}
table.category {
	margin-top: 1em !important;
}
table.category td.list {
	padding: inherit !important;
	padding-top: 0 !important;
}
dl.menu dt, table.category td.header {
	background:#DDDDDD !important;
	color: inherit !important;	
	padding: inherit !important;
	font-weight: inherit !important;
	border: solid 1px #CCCCCC !important;
	-moz-border-radius: 5px !important;
	margin-top: 1em !important;
	padding-left: .5em !important;
	cursor: pointer !important;
}
dl.menu dd, table.category td.list div {
	margin-left: .5em !important;
	padding-left: .5em !important;
	border-left: solid 1px #CCCCCC !important;
}
.list div a {
	display: block !important;
	text-decoration: underline !important;
	padding: 0 !important;
}
.list div a:hover {
	background: inherit !important;
}
dl.menu dd.hidden {
	display: none !important;
}
p.spoiler > span.warning {
	color: #FF6600 !important;
	font-weight: bolder !important;
}
span.spoiler {
	padding-left: 0 !important;
	padding-right: 0 !important;
	background: #BBBBBB !important;
	color: #BBBBBB !important;
}
span.spoiler:hover {
	color: #333333 !important;
}

ol, ul {
	list-style-position: inside !important;
	padding-left: 0em !important;
}
.news {
	background:#DDDDDD !important;
	color: inherit !important;	
	padding: inherit !important;
	font-weight: inherit !important;
	text-align: center !important;
	margin-bottom: 5px !important;
}

.greeting {
	text-align: center !important;
	font-weight: bold !important;
	color: inherit !important;
}




body {
	margin: 0 !important;
	padding: 8px !important;
	margin-bottom: auto !important;
}
blockquote blockquote {
	margin-left: 0em !important
}
form {
	margin-bottom: 0px !important
}
form .trap {
	display: none !important
}
.postarea {
	text-align: center !important
}
.postarea table {
	margin: 0px auto !important;
	text-align: left !important
}
.thumb {
	border: none !important;
	float: left !important;
	margin: 2px 20px !important
}
.nothumb {
	float: left !important;
	background: #eee !important;
	border: 2px dashed #aaa !important;
	text-align: center !important;
	margin: 2px 20px !important;
	padding: 1em 0.5em 1em 0.5em !important;
}
.reply blockquote, blockquote :last-child {
	margin-bottom: 0em !important
}
.reflink a {
	color: inherit !important;
	text-decoration: none !important
}
.reply .filesize {
	margin-left: 20px !important
}
.userdelete {
	float: right !important;
	text-align: center !important;
	white-space: nowrap !important
}
.replypage .replylink {
	display: none !important
}




td.prohibition {
	display: none !important;
}
div.sublogo {
	display: none !important;
}


}

@-moz-document
url-prefix(http://iichan.ru/n/list_),
url-prefix(http://www.iichan.ru/n/list_) {
.logo:before {
	content: "Ð”Ð²Ð°.Ñ‡" !important;
	text-decoration:underline !important;
}

.logo a {
	display: none !important;
}

}

@-moz-document
url-prefix(http://iichan.ru/b/),
url-prefix(http://www.iichan.ru/b/) {
.postername { display:none !important; }
.postertrip { display:none !important; }
.commentpostername { display:none !important; }
}
