css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url(http://sports.yahoo.com/golf/pga/leaderboard){
BODY
{overflow-x:hidden !important;
 margin-left:-5px !important;
 width:288px !important;}
.sportsTable TH
{background-image:none !important;
 background-color:rgb(204,204,204) !important}
.yspTitleBar
{width:288px !important;
 padding-top:0px !important;
 padding-left:0px !important;
 margin-top:-15px !important;
 font-size:x-small !important;
 text-align:center !important}
.sportsTable>THEAD>TR>TH
{padding-left:0 !important;
 padding-right:0 !important}
#yspContentWrapper,
#yspMainContentWrapper,
#leaderboard,#leaderboardtable,
.sportsTable,.sportsTable>THEAD,
.sportsTable>THEAD>TR,
.sportsTable>THEAD>TBODY
{width:288px !important;
 text-align:left !important;}

A,TBODY .total
{color:black !important;
 font-weight: bold !important;}

/* Highlight your favorites */
A[href*="Tiger+Woods"],
A[href*="Phil+Mickelson"]
{background-color:yellow !important;}

#yspHeaderWrapper,#yspSubContentWrapper,#yspFooterWrapper,
.yspPageHeader,.weather,.sevenTen,UL,.last,.clr,.mast,
.header_section,#past_tours,.total+TH,.total+TD
{display:none !important;}}
