css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org") {

/* --- Saxamaphone !3.saxN0DHY PRESENTS --- */
/* --- 4CHAN HARMONY ---*/

/* --- GENERAL PAGE FIXES --- */

/* Blurry Thumbnails Fix */
IMG[md5] {
	height: auto !important;
	width: auto !important;
}

/* Background */
BODY {
	background: #313131 !important;
}

TABLE {
	clear: left !important;
	margin-top: 5px !important;
}

A {
	color: #6699cc !important;
	text-shadow: 0px 0px 5px blue !important;
}

/* NAVIGATION */

#navtop {
	position: fixed !important;
	top: 0px !important;
	right: 0px !important;
	z-index: 3 !important;
	width: 100% !important;
	text-align: center !important;
	color: transparent !important;
	background: rgba(0,0,0,.25) !important;
	padding: 0px !important;
	margin: 0px !important;
}

#navtop A:hover {
	text-shadow: 0px 0px 5px aqua !important;
}

#footer, #navtopr {
	display: none !important;
}

/* BOARD NAME */

.logo > IMG,
.logo > BR,
.logo > FONT[size="1"] {
	display: none !important;
}

.logo B SPAN {
	font-size: 50px !important;
	color: #f0f0f0 !important;
	text-transform: lowercase !important;
	letter-spacing: 1px !important;
}

/* ANNOUNCEMENTS */

CENTER H3 {
	background: #444444 !important;
	margin: 10px !important;
	padding: 5px !important;
	color: #ff3300 !important;
	text-shadow: 0px 0px 5px brown !important;
}

td.doubledash {
	font-size: 1px !important;
	color: transparent !important;
}

td.reply {
background: #444444 !important;
border: 0px solid #993399 !important;
-moz-border-radius: 10px !important;
width: 100% !important;
}

blockquote {
background: #444444 !important;
color: #f0f0f0 !important;
-moz-border-radius: 10px !important;
text-shadow: 0px 0px 10px black !important;
padding: 10px !important;
margin-left: 5px !important;
margin-right: 0px !important;
}

td.replyhl {
background: #545454 !important;
border: 0px solid #993399 !important;
-moz-border-radius: 10px !important;
width: 100% !important;
color: #f0f0f0 !important;
}

td.replyhl blockquote {
background: #545454 !important;
border: 0px solid #993399 !important;
}

.postername, .commentpostername {
padding-left: 5px !important;
color: #99cc33 !important;
text-shadow: 0px 0px 10px black !important;
}

.postertrip {
color: #ff9933 !important;
text-shadow: 0px 0px 10px silver !important;
}

.filetitle, .replytitle {
color: #0099ff !important;
font-variant: small-caps !important;
text-shadow: 0px 0px 10px black !important;
}

.unkfunc {
color: #66cc00 !important;
text-shadow: 0px 0px 10px green !important;
}

.unkfunc a {
color: #ff3300 !important;
text-shadow: none !important;
}

.quotejs:hover {
color: #ff3300 !important;
text-shadow: none !important;
}

hr {display: none !important;}
input[type="checkbox"]:not([name="do_facebook"]) {display: none !important;}

.filesize {
padding: 5px !important;
color: #f0f0f0 !important;
text-shadow: 0px 0px 10px black !important;
}

.filesize a {
padding-right: 2.5px !important;
padding-left: 2.5px !important;
}

.omittedposts {
padding: 5px !important;
color: #f0f0f0 !important;
background: #444444 !important;
-moz-border-radius: 10px !important;
}

.abbr {
color: #f0f0f0 !important;
}

form, td.reply {
color: #f0f0f0 !important;
}

a.linkmail {
text-decoration: none !important;
text-shadow: 0px 0px 10px black !important;
color: #99cc33 !important;
}

span[style^="color"]:not([class="spoiler"]) {
color: pink !important;
text-shadow: 0px 0px 5px purple !important;
font-weight: bold !important;
}

.commentpostername a[href="mailto:sage"]:before {
content: "    SAGE" !important;
color: red !important;
text-shadow: 0px 0px 5px brown !important;
font-weight: bold !important;
padding-right: 5px !important;
}

span[style="left: 5px; position: absolute;"],
a[accesskey="a"],
a[href="../"] {
display: none !important;
}

/* --- POST AREA --- */
.postarea {
position: fixed !important;
z-index: 3 !important;
right: 50px !important;
}

.postarea INPUT {
-moz-appearance: none !important;
border: 1px solid #444444 !important;
}

.postarea INPUT[name="upfile"] {
margin-left: 1px !important;
}

.postarea tr {
opacity: .5 !important;
}

.postarea tr:hover {
opacity: 1 !important;
}

.postarea textarea {
height: 300px !important;
}

.postarea .inputtext {
-moz-appearance: none !important;
border: 1px solid #444444 !important;
width: 300px !important;
margin-left: 2px !important;
}

.postarea input[name=sub] {
width: 234px !important;
}

.postarea .inputtext:focus {
-moz-appearance: none !important;
border: 1px solid #444444 !important;
}

.postarea #fb_tr {
color: #f0f0f0 !important;
text-shadow: 0px 0px 5px black !important;
}

.postarea #fb_tr IMG {
display: none !important;
}

.postarea #fb_tr INPUT {
margin-left: 2px !important;
}

.postarea td.postblock {
width: 75px !important;
padding: 0px !important;
text-align: center !important;
border: 1px solid #444444 !important;
background: #313131 !important;
color: #f0f0f0 !important;
text-transform: lowercase !important;
font-weight: lighter !important;
}

DIV[style="width: 300px; position: absolute; left: 10%; border: 1px solid black;"],
.postarea td.rules,
.postarea small
{ display: none !important; }


/* --- DELETE REPLY --- */

table[align="right"] {
position: fixed !important;
bottom: 10px !important;
right: 10px !important;
background: #444 !important;
-moz-border-radius: 10px !important;
color: #f0f0f0 !important;
padding: 0px !important;
margin: 0px !important;
opacity: .5 !important;
}

table[align="right"]:hover {
opacity: 1 !important;
}

table[align="right"] td[align="right"] {
display: none !important;
}

/* --- PAGES --- */
.pages {
background: transparent !important;
width: 100% !important;
border: 0px !important;
padding: 0px !important;
margin: 0px !important;
text-align: center !important;
}

.pages td {
padding: 0px !important;
color: transparent !important;
}

.pages td b {
color: #ff3300 !important;
}

TABLE.pages INPUT[value="Previous"],
TABLE.pages INPUT[value="Next"] {
display: none !important;
}

}
