css:
@-moz-document url-prefix(http://acm.pku.edu.cn/JudgeOnline/) {
table, table.a, body, tr.in, table tbody tr td, p.pst, p.ptt, th.h, td.h {
    background: black none !important;
    color: white !important;
}
table.a img {
    height: 9pt;
}
div.ptt, div.plm, div.ptx, p.pst, pre.sio {
    font-family: "DejaVu Sans Mono", "Bitstream Vera Sans", "VL Gothic", "Arial", "Helvetica" !important;
}
p.pst {
    font-size: 10pt !important;
}
div.ptx, pre.sio {
    margin-left: 30px;
    font-size: 9pt !important;
}
td.h:hover, td.h {
    background: black none !important;
}
a:link {
    color: #ccccff !important;
}
a:visited {
    color: #ffcccc !important;
}


}
