css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.highercomputingforeveryone.com"), domain("highercomputingforeveryone.com") {


html, body {background: #ccc !important;}

#container {padding: 0 !important; padding-bottom: 10px !Important;}

#companyname {padding-left: 10px !important;}

#top {margin-top:0 !important; border-bottom: 1px solid black !important;}

#top_menu, .toppagenav, #course_menu {background: url("http://www.highercomputingforeveryone.com/images/top_header.gif") no-repeat scroll left top transparent !important;}

#top_menu ul, #course_menu ul {margin:0 !important; padding:0 !important;}
#top_menu ul li, #course_menu ul li {padding-top:0px !important;padding-bottom:0px !important;}
#top_menu ul li a, #course_menu ul li a {color:white !important;}
#top_menu ul li a:hover, #course_menu ul li a:hover {background:none !important; color:black !important;}

/* remove the clutter */
#top_content, #topheader {display:none !important;}

#contentmain {padding-left: 10px !important; padding-right: 10px !important; width:920px !important;}

#right_content {width:190px !important; float:left !important;}
#right_content p {text-align: left !important; text-indent: 10px;}

.coursetitle, .coursetitle a {text-decoration: underline !important;}
.coursetitle:hover, .coursetitle a:hover {color: #53D356 !important;}

.toppagenav a {margin-left: 10px !important;}
.toppagenav, .toppagenav a {font-size: 12px !important; color: #fff !important; border:none !Important;}

.lessontitle a:hover, .unittitle a:hover {color: #53D356 !important;}

.lessontitlecontents, .unittitlecontents {text-indent: 10px !Important;}

p {margin-bottom: 10px !important;}

br {display:none !important;}

a, a:hover, .coursetitle, .lessontitle {cursor: pointer !important; display: inline !Important;}

hr {display:none;}

p br {display: block !Important; margin-top: 2px !important;}

}
