css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('help.rusoft.ru'), domain('help.rusoft.lan') 
{

body,html {
filter: none;
}

#worked_time_otchet {
font-family:monospace !important;
}

#worked_time_otchet b {
font-size:250% !important;
white-space:nowrap !important;
}

table.framed {
background-color:#729fcf;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
padding:1px !important;
  max-width: 230px !important;
}

td.head {
background-color:#3465a4 !important;
border:1px solid #729fcf;
color:#eeeeee !important;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
}

table.framed table {
border-spacing:2px !important;
}

td.silver {
background-color:#eeeeee !important;
}

td.silver td.head {
background-color:#d3d7cf !important;
border:1px solid #babdb6;
color:#2e3436 !important;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
}

td.silver td.head a.ctrlhead:link,
td.silver td.head a.ctrlhead:visited,
td.silver td.head a.ctrlhead:active {
color:#204a87 !important;
text-decoration:underline;
}

td.content {
background-color:#ffffff !important;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
}

.note {
color:#eeeeee !important;
font-weight:bold;
}

.menu {
background-color:#d3d7cf !important;
}

a.edit:link, a.edit:visited, a.edit:active {
color:#cc0000 !important;
}

a:link, a:visited, a:active {
color:#3465a4 !important;
text-decoration:underline !important;
}

a.moveMenuUp, a.moveMenuDown {
color:#fce94f !important;
text-decoration:none !important;
}

#rightzone td.head div a:link, #rightzone td.head div a:visited, #rightzone td.head div a:active {
color:#FFFF00 !important;
text-decoration:none !important;
}

a.ctrlhead:link, a.ctrlhead:visited, a.ctrlhead:active {
color:#fce94f !important;
text-decoration:underline;
}

a.ctrlhead img {
display:none;
}

a.collMenuLink, a.menuCollLink {
color:#fce94f !important;
text-decoration:none !important;
}

td.foot {
border:0 !important;
}

body, td {
font-family:sans !important;
font-size:10pt !important;
color:#2e3436 !important;
}

#menuFilters .menu {
font-size:8pt !important;
}

  div.comment {
    padding-left: 8px !important;
  }
  
  div.comment blockquote {
    padding-left: 8px !important;
    color: #204a87 !important;
    font-style: italic;
    white-space: pre-wrap;
    border-left-style: ridge;
    line-height: 100%;
  }
  
}
