/****************** * * * * * * * * * * * * * * * * * * * *******************
*                               Enjoy Anime theme
*  Author: Noir
*  Date: 4/2/10
*  Description: a theme to replace the current awful one that they have (lol)
*  enjoy!!!!
****************** * * * * * * * * * * * * * * * * * * * *******************/

/*  NOTE: if you want a different image as the header image, scroll down and swap urls
*   it where indicated!
*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.animetake.com") {


body, #header, #outer {
  background:white !important;
  font-family:verdana !important;
  font-size:10px !important;
  color:white!important;
}
#outer{
  background:#dedede !important;
}
#wrapper {
  background:white !important;
}

#wrapper > DIV:first-child, #categories > H3, #rightsidebar > H2:first-child, #calendar_wrap, .textwidget, #about, iframe, .top-banner {
  display:none !important;
}

/*     header bg   */

#header {
  top:0px !important;
  width:100% !important;
  text-align:center !important;
  background: -150px -250px !important;
}

#topbar-outer {
  background: -150px -371px!important;
  border-bottom:3px solid #333333!important;
}

#header > img {
  position:relative !important;
  right:365px !important;
}


.logo {
  width:0px !important;
  background:url("http://i42.tinypic.com/1gisgg.jpg") left top no-repeat !important;
  height:111px !important;
  padding-right:239px !important;
  position:relative !important;
  top:20px !important;
  opacity:1 !important;
  
}
/****************** * * * * * * * * * * * * * * * * * * * *************************/
/*           TO CHANGE THE TOP PICTURE, CHANGE THIS URL TO THE IMAGE YOU WANT!    */
/****************** * * * * * * * * * * * * * * * * * * * *************************/
#header, #topbar-outer {
  background-image: url("http://img88.imageshack.us/img88/1208/konachancom20207147520a.jpg") !important;
}
/****************** * * * * * * * * * * * * * * * * * * * *************************/

/*  http://kuro.hanyuu.net/image/cc568132b42a0da5b59b594d492c7c78/Konachan.com%20-%2069751%20blue_eyes%20brown_hair%20group%20long_hair%20pixiv_fantasia%20short_hair%20sword%20weapon%20white_hair.jpg   */
/* * * * * */

#topbar {
  background:#333333!important;
  -moz-border-radius-topright:5px;
  -moz-border-radius-topleft:5px;
}


/*           margin and padding       */

#mainContent{
  padding:0 !important;
  width:600px !important;
}

#mainContent {
  margin:0 !important;
  margin-right:5px !important;
  margin-left:40px !important;
  padding:0px !important;
}
#rightsidebar {
  margin:0 !important;
  margin-left:5px !important;
  padding:0px!important;
}

#wrapper {
  margin-top:-5px !important;
  padding-top:15px !important;
}

#updates > ul {
  padding:5px !important;
  width:100% !important;
}

.updateinfo {
  width:533px !important;
  position:relative !important;
  left:-10px !important;
}

#rightsidebar > ul {
  padding:5px !important;
}

#rightsidebar > ul > li {
  margin-bottom:2px !important;
}

.listupdate > img {
  height:48px !important;
  width:48px !important;
  border-width:0 !important;
  position:relative !important;
  top:2px !important;
  left:1px !important;
}


.catg_sb{
  position:relative !important;
  left:50px !important;
}

.catg_page{
  position:relative !important;
  left:30px !important;
}

/*         COLORS         */

#topbar > ul > li > a {
  color:#dedede !important;
}

#topbar > ul > li > a:hover {
  background:white!important;
  color:#555555 !important;
  -moz-border-radius:5px !important;
  -moz-box-shadow:0 0 2px white,
  10px 10px 18px #eeeeee inset !important;
}

#topbar > ul > li > a:active{
  -moz-box-shadow:0 0 2px white,
  -10px -10px 18px #eeeeee inset !important;
}
#footer{
  background:#333333!important;
  border-color:black !important;
}

.updateinfo, #rightsidebar > ul > li, .post, .post-info-info, .coment_cont, textarea, #rightsidebar > DIV:last-child > TABLE > TBODY > TR > TD {
  background:white !important;
  border-width:0 !important;
}
#rightsidebar > ul > li:hover, .catg_list > li, #description, .entry, #cont_btm, #content > DIV:last-child > DIV:last-child > DIV, .catg_desc, .catg_genr, .catg_title,
#rightsidebar > DIV:last-child > TABLE > TBODY > TR > TD:hover {
  background:#cecece !important;
  border-width:0 !important;
}
#rightsidebar > DIV:last-child > TABLE > TBODY > TR > TD >a {
  background:none !important;
}
#updates > UL > LI:first-child > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li + li + li + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li + li + li + li + li + li + li + li + li > DIV > IMG:first-child + DIV,
#updates > UL > LI:first-child + LI + LI + li + li + li + li + li + li + li + li + li + li + li + li + li + li + li + li > DIV > IMG:first-child + DIV {
  background:#cecece !important;
  opacity:.95 !important;
}

.listupdate{
  opacity:.85 !important;
  padding:0 !important;
}
.listupdate:hover{
  opacity:1 !important;
}

#updates > H3, #updates, #rightsidebar, #rightsidebar > h2, #updates > H1, #content, .navheader{
  -moz-border-radius-topright:3px;
  -moz-border-radius-topleft:3px;
}
.entry, .post-info-info, .coment_cont {
  -moz-border-radius:3px !important;
}

#updates > H3, #rightsidebar > h2, #updates > H1, .navheader {
  background:#666666 !important;
  color:white !important;
}

#updates, #rightsidebar, #content, .affiliates{
  background:#dedede !important;
}

#wrapper a {
  color:#333333 !important;
}


/*         TEXT/TYPOGRAPHY        */

.cat_list_title, .time {
  color:#6e1221!important;
}

#description, .main, .post > p, .updateinfo{
  color:#111111 !important;
}

.catg_genr, .catg_title, .catg_desc, .catg_desc * {
  color:#434343 !important;
}

.updateinfo > h4 > a {
  font-size:11px !important;
}


.updated {
  background:#333333 !important;
}




/*           LOGIN TOP RIGHT                 */

.login {
  background:none !important;
}
.loggedin em {
  color:#dddddd !important;
  font-size:12px !important;
  position:relative !important;
  right:75px !important;
  top:28px !important;
}
p.center {
  position:relative !important;
  top:-27px !important;
}
#categories {
  position:absolute!important;
  top:-60px !important;
  right:10px !important;
}


/*******        * * * * * *      ********/

#footer:after {
  content:"|| eNJoY ANIME skinned by Noir ||";
  font-size:7px !important;
}


#wrapper A[href="http://www.animetake.com/forums/usercp.php"], #wrapper A[href="http://www.animetake.com/wp-login.php?action=logout"], #wrapper #footer * {
  color:#ffffff!important;
}

A[href="http://www.animetake.com/page/2/"] {
  position:relative !important;
  top:25px !important;
}

#ddmcc_container > DIV:last-child {
  position:relative !important;
  left:50px !important;
}

.loggedin {
  position:relative !important;
  left:-25px !important;
}

#content > DIV:first-child + DIV > P:first-child, .entry.entry-cont > P {
  color:#333333 !important;
}

}
