css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk"){

/*	Name: iichan dark theme */


html, body {
	background-color: #212121 !important;
	color:#678CB1 !important;
 }

input,textarea {
	color: #F0F0F0 !important;
	background: #293134 !important;
	border-color: #3F4B4E !important;
	}
input:focus, textarea:focus {
	border-color: #93C763 !important;
}

a {
	color: #C9BE89 !important;
	text-decoration: none !important;
}
a:hover,.reflink a:hover {
	color: #789922 !important;
}

.logo {
        clear:both;
        text-align:center;
        font-size:2em;
        color:#F0F0F0 !important;
        width:100%;
}
a.logo,a.sublogo {
        color:#F0F0F0 !important;
	}
.sublogo {
        clear:both;
        text-align:center;
        font-size:1em;
        color:#F0F0F0 !important;
        width:100%;
	padding: 2px 0px 12px 0px;
}
.reflink a{
        color:#678CB1 !important;
	}
.theader {
        background:#3A4649 !important;
        text-align:center;
        padding:2px;
        color:#F0F0F0 !important;
        width:100%;
}
.postarea {
}
.postblock {
        background:#3F4B4E !important;
        color:#81969A !important;
        font-weight:800;
}
.footer {
        text-align:center;
        font-size:12px;
        font-family:serif;
	clear: both;
	padding-top: 10px;
}
.passvalid {
	background:#EEAA88;
	text-align:center;
	width:100%;
	color:#ffffff;
}

.managehead {
	background:#AAAA66;
	color:#400000;
	padding:0px;
}
.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#EEEECC;
	color:#800000;
}
.row2 {
        background:#DDDDAA;
        color:#800000;
}
.unkfunc {
	background:inherit;
	color:#789922;
}
.filesize {
	text-decoration:none;
}
.filetitle {
	background:inherit;
	font-size:1.2em;
	color:#F0F0F0 !important;
	font-weight:800;
}
.postername {
    color:#93C763 !important;
	font-weight:bold;
}
.postertrip {
	color:#228854;
}
.oldpost {
        color:#CC1105;
        font-weight:800;
}
.omittedposts {
        color:#707070;
}
.reply {
	background:#293134 !important;
	color:#678CB1 !important;
}
.doubledash {
 	vertical-align:top;
	clear:both;
	float:left;
}
.replytitle {
	font-size: 1.2em;
        color:#E1E2CF !important;
        font-weight:800;
}
.commentpostername {
        color:#93C763 !important;
	font-weight:800;
}
.thumbnailmsg {
	font-size: small;
	color:#707070 !important;
}



.abbrev {
        color:#707070;
}
.highlight {
	background:#2C2C2C !important;
	color:#678CB1 !important;
	border: 2px dashed #678CB1 !important;
}

/* board list */

.logo a, .sublogo a {
        clear:both;
        text-align:center;
        font-size:1.0em;
        color:#F0F0F0 !important;
        width:100%;
}

.category {
	font-family: sans-serif;
	font-size: small;
	margin-bottom: 8px;
	background: #F0E0D6;
}

.header {
    background: #3A4649 !important;
	color: #D6D6D6 !important;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
}

.list {
        border-left: 1px solid #3F4B4E !important;
        border-right: 1px solid #3F4B4E !important;
        border-bottom: 1px solid #3F4B4E !important;
}

.list div a {
	display: block;
	text-decoration: none !important;
	background: #2C2C2C !important;
	color: #93C763 !important;
	padding: 0px 3px 0px 3px;
}

.list div a:hover {
	background: #394144 !important;
}

/* admin posts */

.adminname {
	color: #FF4A4A !important;
}

/* rules */

.rules {
        font-size:0.7em;
}

.rules table {
	font-size: 1.1em;
	margin: 5px 0px 5px 0px;
}

.prohibition {
	color: #66747B !important;
}

.prohibition ul, .info ul {
	margin: 0px;
	padding: 0px 0px 0px 20px;
}

/* title page */

.news {
        background: #F0E0D6;
	color: #800000;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
	text-align: center;
	margin-bottom: 5px;
}

.greeting {
	text-align: center;
	font-weight: bold;
	color: #C8C9B6 !important;
}

.spoiler {
	background: #3A4649 !important;
	color: #3A4649 !important;
}

.spoiler:hover {
	background: #3A4649 !important;
	color: #678CB1 !important;
}

img[alt=""] {
	background: #F0F0F0 !important;
	}
.captcha {
	/* background: #81969A !important; */
	color: white !important;
         }
	/*
	  Foreground color of the letters on pictures generated by captcha.pl.
	  Don't use comments right before declaration of this class.
	*/



}
