css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(hydra.nixos.org) {
body {
background-color: rgb(20,20,20)!important;
color: rgb(200,230,230)!important;
}
h1 {
color:rgb(250,200,200)!important;
}
a:link {
color:rgb(200,255,200)!important;
}

a:visited{
color:rgb(150,255,200)!important;
}
a:hover {
color:rgb(150,255,200)!important;
background-color:rgb(30,30,50) !important;
}

pre, div, code {
font-family:"Terminus"!important;
font-size:14pt!important;
background-color: rgb(30,30,30)!important;
color: rgb(200,230,230)!important;
}
div.book {
background-color: rgb(20,20,20) !important;
}

}
