css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chyrp.net") {

p{ font-family: "Droid Serif", "Palatino Linotype", serif !important;
   font-size: 1.28em !important; }

#navigation h1{ display:none !important;}
#navigation li{ padding-top: 0 !important;}
#navigation li.selected a{ padding-top:.8em !important;
                           padding-bottom:9.7em !important;
                           color: #111 !important; } 
#navigation a{ font-weight:bold;
               text-transform: uppercase;
               display:block;
               padding-top:10em !important;
               letter-spacing:.2em;
}
#navigation a:hover{
  text-shadow: 0 0 150px #FFF, 0 0 60px #FFF, 0 0 10px #FFF;
  /* The above rule is straight outta:
     http://desandro.com/articles/the-new-lens-flare/ */
}

/* this needs to work */
form.search{ margin-top:9.2em !important; }
#query{ content:"SEARCH"; }
#query:focus{ content: ""; }

.first{ width: 6em !important;}
.second{ width: 10.5em !important;}
.third{ width: 8.8em !important;}
.fourth{ width: 9.8em !important;}
.fifth{ width: 8em !important;}
.sixth{ width: 6.5em !important;}
.seventh{ width: 6.3em !important;}

.first a:before,
.second a:before,
.third a:before,
.fourth a:before,
.fifth a:before,
.sixth a:before,
.seventh a:before{
  content: "Chyrp chyrpity chyrp";
  text-transform: Capitalize;
  letter-spacing: normal;
  font-size:4.8em; /* change this at the your own peril */
  font-family: "Droid Serif", Georgia, "Palatino Linotype", serif;
  position:absolute; left:0; top:1em; font-style:italic; display:block; white-space:nowrap;}

/* What follows is the careful alignment of the generated content
   produced in the previous ruleset and if the font-size there
   changes then the following will "break" (try it!) */
.second a:before{ left:-1.252em;}
.third a:before{ left:-3.13em;}
.fourth a:before{ left:-4.74em;}
.fifth a:before{ left:-6.49em;}
.sixth a:before{ left:-7.95em;}
.seventh a:before{ left:-9.11em;}

}

@-moz-document url-prefix("http://chyrp.net/download/") {
  .first a:before,
  .second a:before,
  .third a:before,
  .fourth a:before,
  .fifth a:before,
  .sixth a:before,
  .seventh a:before{ content: "Chyrp Downloads";}
}

@-moz-document url-prefix("http://chyrp.net/discuss/") {
  .first a:before,
  .second a:before,
  .third a:before,
  .fourth a:before,
  .fifth a:before,
  .sixth a:before,
  .seventh a:before{ content: "Chyrp Discussions";}
}

@-moz-document url-prefix("http://chyrp.net/progress/") {
  .first a:before,
  .second a:before,
  .third a:before,
  .fourth a:before,
  .fifth a:before,
  .sixth a:before,
  .seventh a:before{ content: "Chyrp Progression";}
}

@-moz-document url-prefix("http://chyrp.net/extend/") {
  .first a:before,
  .second a:before,
  .third a:before,
  .fourth a:before,
  .fifth a:before,
  .sixth a:before,
  .seventh a:before{ content: "Chyrp Extensions";}
}

@-moz-document url-prefix("http://chyrp.net/20") {
  .first a:before,
  .second a:before,
  .third a:before,
  .fourth a:before,
  .fifth a:before,
  .sixth a:before,
  .seventh a:before{ content: "Chyrp blog on chyrp";}
}
