css:
/*--------------------------------------------------------------------------------
  eBuddy - Better for Sidebar (Version 0.2.0)
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows XP Firefox 3.6.2 - Last Update: 03.04.2010
  --------------------------------------------------------------------------------
  Description:
    - Remove ad sidebar
    - Reduce login page, Dialogs width 
    - Resize User images
  --------------------------------------------------------------------------------*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ebuddy.com") {
 #banner_skyscraper, #banner_rectangle, #banner_rectangle_iframe, #banner_rectangle_skip{
  display:none !important;
  text-indent:0 !important;
 }

 #all-inner{
  right:0 !important;
 }

 #login_form h1 {
  background:url("http://lcontent.ebuddy.com/multi/vo7.7.7/themes/default/images/ui/top_bar/topbar_bg_hatch.gif") no-repeat scroll left transparent !important;
  text-indent:-999999px !important;
  height:30px !important;
 }

 .contacts_tree {
  width:100% !important;
 }

 .combo_login_container, .login_input {
  width:150px !important;
 }

 #login_submit_cont {
  width:150px !important;
  margin-top: 30px !important;
 }

 .login_table {
  width:200px !important;
 }

 #dialog_alert {
  width:250px !important;
 }
 
 .login > center {
  display:none !important;
 }
 
 body {
  width:90% !important;
 }

 .treefilter {
  width: 20px !important;
 }

 .treefilter:focus {
  width: 120px !important;
 }

 #combo_sorting {
  right:8px !important;
  text-indent:-999999px !important;
  background:none !important;
 }

 .treefilter {
  right:52px !important;
 }

 .treefilter_clear {
  right:32px !important;
 }

 #sidetabs_top {
  display:none !important;
 }

 #sidetabs_content {
  border-left: 0 !important;
 }
 
 .tabset_scroller {
  right:0  !important;
 }

 .contact_context_btnwebcam, .contact_context_btnhistory, .contact_context_btninfo, .contact_context_btnblock, .contact_context_btndel, .contact_context_btnchat {
  opacity: 0.5 !important;
 }

 #contact_context_info {
  left:15px !important;
 }

 #contact_context_dp {
  height:30px !important;
  width:30px !important;
  margin-top: 40px !important;
 }
 
 .chat_list_contacts {
  width:30px !important;
  border:0 !important;
 }

 .chat_list_contacts img, .chat_list_contacts div {
  width:30px !important;
  height:30px !important;
  border:0 !important;
  margin:0 !important;
  padding:0 !important;
 }

 .chat_editor_area {
  border-right:0px solid !important;
 }

 .chat_hold {
  border-right:30px solid #fff !important;
 }

 #label_account {
  text-indent:-999999px !important;
 }

 #label_account:hover {
  text-indent:0px !important;
 }

.chat_user_icon, .chat_user_icon img {
  height:30px !important;
  width:30px !important;
  margin: 0 0 0 0 !important;
 }

 .dialog_main {
  width:90% !important;
 }

 #notifications {
  /*display:none !important;*/
 }
}
