css:
/*
 * YouTube - dark gray redesign
 *
 * Author      : Osvifr
 * Updated     : 08.08.2012
 * Description : Modifies the colors, logos, and a few other images on YouTube (http://youtube.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("youtube.com")
{
/* Main */

    body {background: #000 !important; color: #ddd !important;}

a:link {  color:#999 !important; text-decoration:none !important; }
a:visited {  color:#666 !important; text-decoration:none !important; }
a:focus { color:#fff !important; text-decoration:none !important;}
a:hover {  color:#bbb !important; text-decoration:underline !important; }
a:active { color:#777 !important; text-decoration:underline !important; }

#default-language-box {background: #333 !important;}


.horizontal-rule{border: 1px solid #888 !important;}

/*Startpage */
#feed {width: 470px !important;}
#feed {background: #333 !important;}
#feed .feed-item-container div {background: #333 !important; color:#aaa !important;}
#feed .feed-item-container .title {color:#ddd !important;}
#feed .feed-item-container .feed-item-title {background: #666 !important; color: #ddd !important}
#feed .feed-item-container .feed-item-title span{color: #ddd !important}
#feed .feed-item-container .feed-item-title span a{color: #fff !important}
#video-sidebar {background: #272727 !important;}
#video-sidebar p, #video-sidebar h3 { color:#aaa !important;}
#video-sidebar h2 { color:#eee !important;}
#video-sidebar ul li span {color:#aaa !important;}
#video-sidebar ul li .title {color:#ddd !important;}
#video-sidebar ul li a:hover {background: #111 !important; border: 1px solid #111 !important;}

/* Ads */
#homepage-sidebar-ads,
#watch-channel-brand-div {display: none;}

/* masthead-expanded */
#masthead-expanded-container {background: #333 !important; color:#ddd !important;}
#masthead-expanded .playlist-bar-item {background: #333 !important; border: 1px solid #111 !important;color:#ddd !important;}
#masthead-expanded .playlist-bar-item .playlist-data-section .playlist-data-item {color:#ddd !important;}
#masthead-expanded .playlist-bar-item .playlist-data-section .list-video-facets{color:#aaa !important;}
#masthead-expanded .yt-uix-button{background: #222 !important; border: 1px solid #111 !important;color:#ddd !important;}
#masthead-expanded .yt-uix-button:hover{background: #666 !important; border: 1px solid #111 !important;color:#ddd !important;}


/* Search Bar */
#masthead-search #masthead-search-terms {background: #444 !important; color:#ddd !important;}
#search-btn {background: #bbb !important; color:#ddd !important;}
#masthead-search-terms {border: 1px solid #999 !important;}
#masthead-search-term {background: #454545 !important; color:#fff !important;}
#masthead-search-bar .focused #masthead-search-terms {border: 1px solid #ccc !important;}


/* Header */
#masthead-user-wrapper {border: 0px solid #000 !important;}
#masthead-user-button {color:#ddd !important; border: 0px solid #444 !important; background: #444 !important; box-shadow: none !important;}
#masthead-user-button:hover{color:#ddd !important; border: 0px solid #666 !important; background: #666 !important; box-shadow: none !important;}
#page.watch #masthead-container {background: none #000 !important;}


/* Footer */

#footer-main a { color:#eee !important;}
#footer-main .yt-uix-button {color:#ddd !important; border: 1px solid #333 !important; background: #222 !important; box-shadow: none !important;}
#footer-main .yt-uix-button:hover {color:#ddd !important;  background: #444 !important; box-shadow: none !important;}

#region-picker, #language-picker, #safetymode-picker{background: #222 !important;}

/* Video Page */
#watch-container,
#watch-video-container{background: #000 !important;}
#watch-headline h1{color:#fff !important;}
#watch-headline-user-info .yt-uix-button {background: #343434 !important; border: 1px solid #111 !important;color:#ddd !important;}
#watch-headline-user-info .yt-uix-button:hover {background: #666 !important; border: 1px solid #111 !important;color:#ddd !important;}

#watch-sidebar {background: #272727 !important;}
#watch-sidebar ul li a{background: #333 !important; border: 1px solid #111 !important;}
#watch-sidebar ul li span{color:#aaa !important;}
#watch-sidebar ul li .title{color:#ddd !important;}
#watch-sidebar ul li a:hover{background: #111 !important; border: 1px solid #111 !important;}
#watch-actions .watch-view-count strong{color:#aaa !important;}
#watch-actions .yt-uix-button {background: #343434 !important; border: 1px solid #111 !important;color:#ddd !important;}
#watch-actions .yt-uix-button:hover {background: #666 !important; border: 1px solid #111 !important;color:#ddd !important;}
#watch-actions-area {background: #222 !important;}
#watch-like-share .share-panel-url {border: 1px solid #bbb !important; background: #bbb !important; color:#000 !important;}


#watch-info #watch-description {background: #333 !important; border: 1px solid #111 !important;color:#aaa !important;}
#watch-description-toggle div {background: #333 !important; border: 1px solid #222 !important;color:#ddd !important;}
#watch-description-toggle div button{background: #222 !important; border: 1px solid #111 !important;color:#ddd !important; text-shadow: none !important;}
#watch-description-toggle div button:hover{background: #555 !important; border: 1px solid #222 !important; color:#ddd !important;}
#watch-discussion .comment-list li {background: #333 !important; border: 1px solid #000 !important;color:#ddd !important;}
#watch-discussion .comment-list li:hover {background: #444 !important; border: 1px solid #111 !important;color:#ddd !important;}
#comments-view {background: #222 !important; border: 1px solid #111 !important; color:#ddd !important;}
#comments-view .comments-post-alert {background: #444 !important;  border: 1px solid #666 !important;}

#watch-more-from-user .video-list-item:hover {background: #333 !important;}
#watch-more-from-user .video-list-item span { color:#ddd !important;}
#watch-more-from-user .yt-tile-visible {background: #666 !important;  border: 1px solid #666 !important;}

.watch-playlists-drawer ul {background: none repeat scroll 0 0 #111111 !important; }



/* Search Results */
#search-header h2 {color:#fff !important;}
#search-header h2 li{color:#ddd !important; font-style: italic;}
#search-header p{color:#ddd !important; }

#search-results .result-item:hover {background: #333 !important;}
#search-results .result-item a.yt-uix-tile-link{color:#fff !important;}
#search-results .result-item p{color:#aaa !important;}
#search-results .result-item p b{color:#ddd !important;}

#search-refinements button{background: #333 !important; color:#aaa !important; border: 1px solid #444 !important;}
#toolbelt-top .search-option a:hover{background: #666 !important;}

}
