@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Combine Stop/Reload Buttons (stop button must be placed before reload button on toolbar) */
#stop-button[disabled="true"] {display:none;}
#stop-button:not([disabled]) + #reload-button {display:none;}

/* Hide bookmark star */
#star-button {display:none !important;}

/* Back */
#back-button[disabled="true"] 
{ 
  display: none; 
}



/* Forward button */
#forward-button[disabled="true"] 
{ 
  display: none; 
}




/* Dropmarker - List of Forward/Back URLS  Will disable if both back and forward are disabled: */
#back-forward-dropmarker[disabled="true"]
{
  display: none; 
}


/*REMOVE URL ID BUTTON, STAR BUTTON, MAGNIFYING GLASS*/
#go-button  {display:none !important}
#identity-box  {display:none !important}
.search-go-button  {display:none !important}
/*#star-button {display:none !important}*/
#feed-button {display:none !important}
/*.searchbar-engine-button {display:none !important}*/

/* Remove the Edit, Help, and history menus
   Id's for all toplevel menus:
   file-menu, edit-menu, view-menu, go-menu, 

#history-menu, Bookmarks-menu, #bookmarks-menu, tools-menu, helpMenu 
#helpMenu, #edit-menu, #history-menu {    display: none !important; } 
*/
