css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
* { font-family: "Helvetica Neue", Helvetica, "MgOpen Moderna", sans-serif !important;}
/*-------------------
        LINKS
-------------------*/
.title a,.title  a:visited {
  color: #1e90ff !important;
}
.title a:hover,.title  a:active,.entry .buttons li a:hover {
  color: orangered !important;
}
.entry .buttons li a {
  color: #888 !important;
}
/*-------------------
       HEADER
-------------------*/
#sr-header-area, #sr-more-link {
  background: #cee3f8 !important;
  border-bottom: none /*1px double #111*/ !important;
  font-size: 10px !important;
 }
    #sr-header-area a {
      color: #97c2ed !important;
    }
    #sr-header-area:hover a {
      color: #1e90ff !important;
    }
    #sr-header-area a:hover {
      color: orangered !important;
      text-decoration:none !important;
    }
    #sr-header-area .separator {
      color: #bbb !important;
    }
    #sr-header-area:hover .separator {
      color: #666 !important;
    }

    #sr-bar .selected a,#sr-more-link, .tabmenu li.selected a{
      color: orangered!important;
    }
    #sr-header-area #sr-more-link {
      color: #e19173 !important;
      margin: -3px 0 0 !important;
      padding-top: 3px !important;
      height: 15px;
      border-bottom: none !important;
    }
    #sr-header-area:hover #sr-more-link {
      color: orangered !important;
    }
#header-bottom-right {
  background: #fff !important;
}
/*-------------------
     MY REDDITS
-------------------*/
.dropdown.srdrop .selected {
  /*text-indent: -100em !important;*/
  color: #1e90ff !important;
  -moz-border-radius-topright: 5px;
  -moz-border-radius-bottomright: 5px;
  background: #fff right no-repeat url("data:image/gif;base64,R0lGODlhEAAQALMKAP///9FHE/9xPP9yPv9qM65TMcZLHf9aHf9pMf91Qv///wAAAAAAAAAAAAAAAAAAACH5BAEAAAoALAAAAAAQABAAAAQwUMlJq704Y8C7BkEYAF9ACOPGmahXAUU4DGFBWqCQDEi6GYKDLwMwDD83jXLJbGYiADs=") !important;
}
    #sr-header-area:hover .dropdown.srdrop .selected, #sr-header-area .drop-choices a {
      color: #1e90ff !important;
    }
.drop-choices {
top: 17px !important;
left: 0 !important;
background: #fff !important;
-moz-border-radius-topright: 7px;
-moz-border-radius-bottomright: 5px;
border: 1px double #e2efff !important; 
}
    .drop-choices a:hover {
    background: #e2efff !important;
    }
/*-------------------
      TABMENU
-------------------*/
#header {
  border-bottom: 1px solid #e2efff !important;
}
.tabmenu li {
  font-weight: normal !important;
  border-bottom: 1px solid #e2efff !important;
}
.tabmenu li.selected a, .tabmenu li a {
  -moz-border-radius-topleft: 7px;
  -moz-border-radius-topright: 7px;
}
.tabmenu li a{
  background: #eff7ff!important;
  color: #1e90ff !important;
}
.tabmenu li a:hover{
  background: #fff !important;
  color: #1e90ff !important;
  border-bottom: 1px solid #fff !important;
}
.tabmenu li.selected a {
  background: #fff !important;
  font-weight: bold !important;
  border: 1px solid !important;
  border-color: #e2efff #e2efff #fff !important;
}

/*-------------------
       SIDEBAR
-------------------*/
.side {
  width: 225px !important;
  background: none !important;
}
#search input[type="text"] {
  background: #e2efff 4px 2px no-repeat url("data:image/png;base64,") !important;
  color: #8ac1f7 !important;
  margin: -8px 0 0 4px !important;
  padding: 0 2px 0 1px !important;
  height: 17px !important;
  width: 221px !important;
  text-align: right;
  font-size: 12px !important;
  -moz-border-radius-bottomleft: 7px;
  border-color: #e2efff !important;
}
#search input[type="text"]:hover {
  background-color: #e2efff !important;
  border-color: #e2efff !important;
  color: #22598f !important;
}
.side .submit {
  float: right !important;
  margin-top: -16px !important;
  margin-right: -1px !important;
  width: 100px;
}
.side .create {
  margin-left: 3px !important;
  margin-top: -16px !important;
  width: 150px;
}
  .submit .morelink{
    text-align: right !important;
  }
  .morelink {
    font-size: 12px !important;
    font-weight: normal !important;
    text-align: left !important;
    background: none !important;
    border: 0 !important;
  }
  .morelink a:hover {
    color: #336699 !important;
  }
.morelink .nub ,.sidebox .spacer,.sidecontentbox,.side .usertext{
  display: none !important;
}

.fancy-toggle-button .remove, .fancy-toggle-button .active {
  color: #333 !important;
}

.linkinfo {
-moz-border-radius:7px 0 0 7px !important;
border-right: 0 !important;
margin-right: -5px !important;
}


/*-------------------
     REDDITS
-------------------*/
a.thumbnail {
  text-align: center !important;
  border: 1px solid #e2efff;
  width: auto !important;
  background: #fff !important;
}
a.thumbnail:hover {
  border: 1px solid #7cd8ff;
}
.thumbnail img {
  padding: 4px !important;
}
.score {
  padding-top: 2px !important;
  font-size: 12px !important;
  font-weight: normal !important;
  color: #666 !important;
}
.rank {
  display: none !important;
}
.thumbnail,.entry {
    margin: 0 10px 0 0 !important;
}
.rank,.midcol {
    padding-top: 5px !important;
}
.odd {
  background: #fff !important;
}

/*-------------------
     COMMENT
-------------------*/
.infobar {
  color: #cee3f8 !important;
  background: #333 !important;
  border: 1px double #333 !important;
  -moz-border-radius: 7px;
}
textarea,.usertext button {
  color: #1e90ff !important;
}
.usertext .save {
  color: orangered !important;
  background: #000 left no-repeat url("data:image/png;base64,") !important;
}
.markhelp {
  border: 1px solid #e2efff !important;
  -moz-border-radius: 5px !important;
  -moz-border-radius: 7px;
  border-collapse: separate !important;

  color: #000;
  background: #f0f7ff !important
}
.markhelp td {
  background: #e2efff !important;
  border-color: #d2e6ff !important;
}
.help-toggle .option{
  padding-right: 20px !important;
  font-size: 12px !important;
  background: right no-repeat url("data:image/png;base64,") !important;
}
textarea[name="text"] {
}
/*-------------------
    SUBMIT NEW
-------------------*/
.tabmenu.formtab a {
  background: #333 !important;
  color: #cee3f8 !important;
  text-transform: uppercase;
}
.tabmenu.formtab a:hover {
  background: #555 !important;
  color: #fff !important;
}


.formtabs-content {
  border-top: 2px double #333 !important;
}

.roundfield {
  background: none !important;
}

.roundfield textarea,.roundfield input[type="text"], .roundfield input[type="password"]  {
  background: #333 !important;
  -moz-border-radius: 7px;
  color: #cee3f8 !important;
}
.roundfield .title {
  text-transform: uppercase;
}
.searchpane #search input[type="text"] {
  text-align: left !important;
  color: orangered !important;
  margin-top: 0 !important;
  background: #fff !important;
  -moz-border-radius: 3px;
  border-color: #333 !important;
}
/*---------------
   READ MORE
---------------*/
.link .usertext .md {
  background: #f0f7ff !important;
  border: 1px solid #e2efff !important;
  color: #444 !important;
}
.nsfw-stamp {
  background: orangered !important;
}
.rounded {
  border: 1px solid #e2efff !important;
  -moz-border-radius: 5px !important;
}
/*---------------
     FOOTER
---------------*/
.footer {
  background: #f0f7ff !important;
}
/*--------------
 SPRITE + IMAGES
---------------*/
.midcol .down, .likes .down,.midcol .up, .dislikes .up,.dislikes .downmod,.likes .upmod,.expando-button.selftext.collapsed,.expando-button.selftext.collapsed:hover, .eb-sch,.expando-button.selftext.expanded, .eb-se,.expando-button.selftext.expanded:hover, .eb-seh,.expando-button.video.collapsed:hover,.expando-button.video.collapsed, .eb-vch,.expando-button.video.expanded, .eb-ve,.expando-button.video.expanded:hover, .eb-veh,.morelink:hover .nub, .mlhn {
  background-image: url("data:image/png;base64,") !important;
}
.midcol .down, .likes .down {
   background-position: -4px -246px !important;
}
.dislikes .downmod {
   background-position: -4px -201px !important;
}
}
