css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theonlinecitizen.com") {

body {
  background-color: #fff !important;
}

#header, #contain-bg {
  background-image: none !important;
}

#header {
  height: 90px !important;
  padding: 1px 20px 10px !important;
  border: 0 !important;
}

.widgetbox-ad1, .ad540x90, .ad590x90, #subscribe, .widget iframe, div.postmeta > span.left, .sexy-bookmarks {
  display: none !important;
}

.entry .sexy-bookmarks + p {
  font-size: 24px !important;
  border-bottom: 1px solid #ddd;
  padding-bottom: 0 !important;
  margin-bottom: 3px !important;
}

.entry .sexy-bookmarks + p + ol {
  margin: 8px 0 12px !important;
}

#inner {
  padding: 0 30px 30px 30px !important;
  width: 900px !important;
  overflow: hidden !important;
}

#content, #column1, .postbox, #breaking {
  width: 570px !important;
  position: relative !important;
}

.postbox {
  padding: 10px 0 35px !important;
}

#featured {
  margin-bottom: 25px !important;
}

#column1 {
  margin-top: 30px !important;
}

#paths {
  -moz-border-radius: 0 0 8px 8px;
  width: 440px !important;
  margin-bottom: 20px !important;
  padding: 3px 10px 5px 10px !important;
  border-top: 0 !important;
  -moz-box-shadow:0 1px 1px rgba(0, 0, 0, 0.1);
}

h2.posttitle {
  font-family: Georgia, serif !important;
  font-weight: normal !important;
  font-size: 30px !important;
  margin: 20px 20px 0 0 !important;
  line-height: 1.2em !important;
}

span.comment.right {
  position: absolute !important;
  top: 0px !important;
  right: 10px !important;
  clear: both !important;
  font-size: 10px !important;
  -moz-border-radius: 0 0 8px 8px !important;
  background-position: 5px center !important;
  border: 1px solid #DDDDDD !important;
  padding: 3px 8px 4px 28px !important;
  border-top: 0 !important;
  -moz-box-shadow:0 1px 1px rgba(0, 0, 0, 0.1);
  background-color: #fff;
}

.widgetbox {
  border: none !important;
}

a.imagelogo {
  margin: 0 !important;
}

#menu {
  border-top: 0 !important;
  height: 30px !important;
}

#menu .right {
  position: absolute !important;
  top: -50px !important;
  right: 50px !important;
}

#menu > ul {
  margin: 0px 0 0 30px !important;
}

#menu > ul > li > a {
  text-transform: none !important;
  font-size: 10px !important;
  font-family: Arial, sans-serif !important;
  padding: 7px 12px 9px !important;
  border-right: 1px #999 solid !important;
  color: #fff !important;
}

#menu > ul > li ul li a {
  font-size: 12px;
  padding: 7px 12px 8px !important;
  border-right: 1px #999 solid !important;
  width: auto !important;
  color: #333 !important;
}

.entry blockquote {
  position: relative !important;
  padding: 20px 40px 1px 40px !important;
}

.entry blockquote:before {
  content: '\201C';
  left: -32px;
  top: 42px;
  font-size: 16em !important;
  font-family: Arial !important;
  font-style: normal !important;
  color: #ddd !important;
  position: absolute !important;
}

.entry blockquote:after {
  content: '\201D';
  right: -32px;
  bottom: -62px;
  font-size: 16em !important;
  font-family: Arial !important;
  font-style: normal !important;
  color: #ddd !important;
  position: absolute !important;
}

#menu > ul > li:first-child a {
  border-left: 1px #999 solid !important;
}

#menu > ul ul.children {
  top: 28px !important;
  margin-left: -1px !important;
  border: 1px solid #AFAFAF !important;
  -moz-box-shadow: 0 2px 3px rgba(0,0,0,0.4);
  width: 150px !important;
}

#menu > ul > li a:hover {
  background-color: #666 !important;
  color: #fff !important;
}

.entry p {
  font-family: Georgia, serif !important;
  font-size: 14px !important;
  line-height: 1.5em !important;
  margin-bottom: 0.6em !important;
}

.breakingbox {
  width: 280px !important;
  height: 75px !important;
  margin-bottom: -8px !important;
  margin-right: 0 !important;
  border: 0 !important;
}

.breakingbox h2 {
  float: right;
  padding: 5px !important;
  width: 125px;
}

.breakingbox h2 > a:nth-child(2) {
  color: #999 !important;
  font-weight: normal !important;
  font-size: 9px !important;
}

.boxrightfeatured {
  width: 230px !important;
}

.boxleft, .boxright {
  width: 275px !important;
  overflow: hidden !important;
}

h1.widgettitle, h2.pagetitle, #toctv {
  font-size: 24px !important;
  font-family: Arial, sans-serif !important;
  font-weight: bold !important;
  text-transform: none !important;
}

h2.pagetitle {
  font-size: 30px !important;
  margin: 5px 0 !important;
  padding: 5px 0 12px !important;
}

h1.widgettitle > a, #toctv > a {
  display: block !important;
  padding: 8px 0  !important;
}

#sidebar {
  margin-top: 30px !important;
}

#footer {
  margin: 20px !important;
  width: 585px !important;
}

.archive p.left {
  height: 120px !important;
}

#comments li {
  padding: 10px 0 !important;
  border: 0 !important;
}

.tabs li a {
  font-family: Georgia, serif !important;
  font-weight: normal !important;
  text-transform: none !important;
}

.widgetnews span.grey {
  line-height: 1em !important;
}

}
