css:
@-moz-document domain(gaiaonline.com){
	#grid_ad{
		display:none!important;
	}
	#grid_ad.recycled{/* for gm scripts that make use of the add */
		display:block!important;
	}
}
@-moz-document url-prefix("http://www.gaiaonline.com/forum/compose/"){
	#messagebox{
		width:850px!important;
	}
}
@-moz-document url-prefix("http://www.gaiaonline.com/marketplace/itemdetail/"){
	#gaia_content #item_info .box_content_stretched {
		width:915px!important;
	}
	#gaia_content .box_header_stretched, div#gaia_content .box_footer_stretched {
		width:919px!important;
	}
	#gaia_content #item_info {
		width:910px!important;
	}
	#gaia_content .box_header_stretched {
		background-image:url('data:image/png;base64,')!important;
	}
	#gaia_content .box_footer_stretched{
		background-image:url('data:image/png;base64,')!important;
	}
	#gaia_content #item_basicdata {
		width:800px!important;
	}
	#gaia_content #preview_modal {
		float:right!important;
	}
	#gaia_content #aquariumContainer{
		width: 200px!important;
		float:right!important;
	}
}
