css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("news.google.com") {
/***************************************************
  Version: 1.5 2/2/2010
  Helvetinews for Google News
	http://www.frailamerica.com/helvetinews
/***************************************************/
* {font-family: "Helvetica Neue", Helvetica, "MgOpen Moderna", sans-serif !important;}

body {
  font-size:12px!important;
  color:#333!important;
  background:#fff !important;
  }

body#advanced-search {background:#e6e6e6 url(http://img30.imageshack.us/img30/6108/84740764.gif) top left repeat-x!important;}

.maincontents .centered {padding:0 9px!important;}

#advanced-search #logo {
  padding-top:18px!important;
  margin-top: 0!important;
  }

img, .gbh,.gbd, .bt-border, .youtube-icon, .nav .sidebar, .story {border:none!important;}

#hidden-left-nav-menu li:hover, a.gb2:hover, .large-section .bar, tr td.nub-item:hover, div.more-link, div.fewer-link  {background:#f1f1f1!important;}

#hidden-left-nav-menu, #gbi,#gbg,#personalize-open, .cluster-summary-chart img, .gadget iframe {
  border:none!important;
  border:1px solid #999!important;
  }

#hidden-left-nav-table .hover a, 
a, .sub-header .lt a, .sub-header .lt a:visited, .rank-sorting-nfe a:visited, .thumbnail .source, #pagination .next a, #pagination .prev a, #pagination .next a:visited, #pagination .prev a:visited, #advanced-search .basic-title a, #personalize a:visited, .footer .links a, .notify a:visited, a font {
  color:#15395f!important; 
  text-decoration:none !important;
  }

h2 {font-size:17px!important;color:#333!important;}

#gbar, #guser, .gaiaNav, .basic-title .small, div#guser b, .rank-sorting-nfe,
.sub-header .basic-title .text, .versions, .rt .sel {
  font-size:11px!important;
  color:#333!important;
  }

#gbar a, #guser a, .gaiaNav a, .edit-this-page a, .basic-title .textview-links a, .basic-title .textview-links a:visited, .versions a {
  color:#aaa!important;
  font-size:11px!important;
  font-weight:700!important;
  text-decoration:none!important;
  }


#gbar u {
  text-decoration:none!important; 
  vertical-align:top!important;
  }

#gbar a:hover, #guser a:hover, .gaiaNav a:hover, .edit-this-page a:hover, #page-header form input[type="submit"]:hover, input[type="submit"]:hover, #personalize-open .save input:hover {color:#999!important;}

.snippet {
  font-size:1em!important;
  line-height:1.5em!important;
  margin-bottom:6px!important;
  }

.headline-story {margin-bottom:9px!important;}

.t-FEATURED_IMAGE .picture-story {width: 60%!important;}

.thumbnail img, .picture-story img  {background: #fff; padding: 4px; border:1px solid #bbb!important;}

.tile {border:none!important;}

#mc-quote .icon, .quote-story .speaker-nub, .quote-story .info-nub {background-image:url(data:image/gif;base64,)!important;}

hr, .footer-disclaimer, .footer .bottom-search, .footer .bottom, .rhs-ads-panel, .nav .nub, #advanced-search .divider {display:none!important;}

.search img,
#advanced-search #logo img {
  height:0!important;
  width:0!important;
  padding-left:170px!important; 
  padding-top:40px!important;
  margin-left:6px!important;
  margin-right:18px!important;
  background:#fff url(data:image/png;base64,) 0 -8px no-repeat !important;
  border:none!important;
  }

.search-in-header {height:42px!important;}

.search-in-header form {
  width:800px!important;
  float:right!important;
  }
  
textarea, input, button {
  -moz-appearance:none!important;
  background:none!important;
  font-size:12px!important;
  }
     
.clustersearch-box .q {
  width:80%!important;
  background:#fff!important
  }
  
.sidebar .clustersearch-box .q {width:90%!important;}

#page-header input.searchField, input[type="text"] {padding:4px!important;}  
  
#page-header input[type="submit"], #page-header .search-box-links {
  display:none!important;
  margin-top:4px!important;
  }

#page-header form:hover input[type="submit"], #page-header form:hover .search-box-links {display:inline!important;}

#page-header form input.searchField, input[type="text"] {border:1px solid #ddd!important;}

#page-header form input.searchField:hover, #page-header form input.searchField:focus,  input[type="text"]:hover, input[type="text"]:focus {border:1px solid #aaa!important;}

.edit-panel input.lt,
#page-header form input[type="submit"],
input[type="submit"],
#personalize-open .save input {
  margin-left:6px!important;
  color:#aaa!important;
  font-weight:700!important;
  background-color:transparent!important;
  border:none!important;
  cursor:pointer!important;
  }
  
.gadget, .t-IN_THE_NEWS, .t-HOT_QUERY, .t-BREAKING_NEWS  {
  background-color:#efefef!important;
  -moz-border-radius:10px!important;
  -webkit-border-radius:10px!important;
  padding:9px!important;
  margin-bottom:18px!important;
  width:95%!important;  
  }

.gadget embed, .gadget object {width:99%!important;}

.t-IN_THE_NEWS, .t-HOT_QUERY, .t-BREAKING_NEWS {border-top:none!important;}

.large-section .edit-standard-inner .r, .large-section .edit-custom-inner .r, .large-section .edit-local-inner .r, .large-section .edit-recommended-inner .r, .large-section .edit-shareable-inner .r, #image-tiles .highlight, .ivi-story .hover {background-color:#efefef!important;}

.edit-panel .item input,
.r {background-color:#fff!important;}

.basic-title, .bt-border{background-color:transparent!important;}

.icon,
.close-icon,
.more-zippy-toggle .icon,
.additional-video .more-zippy-toggle .icon,
.email-icon,
.large-section .basic-title a.edit-link,
.large-section .basic-title a.edit-link-close,
.stock-tckr  {
  border:none!important;
  margin:0!important;
  padding:0!important;
  height:16px!important;
  width:16px!important; 
  text-indent:-9000px!important;  
  background-repeat:no-repeat!important;
  vertical-align:middle!important;
    background:url(data:image/png;base64,) no-repeat left center!important;
  }

.unstarred { background-position: -32px -16px !important; } 

.starred { background-position: -48px -16px !important; }

.star-nav {
  margin-bottom: 4px!important;
  margin-left: 4px!important;
  }

.youtube-icon { display:none !important; }  

.large-section .basic-title a {
  font-size:18px!important;
  color:#333!important;
  }

.large-section .basic-title a.edit-link,
.large-section .basic-title a.edit-link-close {background-position:0px -48px!important;}

.large-section .basic-title a.edit-link:hover,
.large-section .basic-title a.edit-link-close:hover {background-position:-16px -48px!important;}

.edit-panel, .large-section .edit-standard-inner, .large-section .edit-custom-inner, .large-section .edit-local-inner, .large-section .edit-recommended-inner, .large-section .edit-shareable-inner {
  background-color:#efefef!important;
  border: none!important;
}
div.close-icon {background-position:-32px 0!important;}
  
div.close-icon:hover{background-position:-48px 0!important;}

.more-zippy-toggle .icon {background-position:0px -32px!important;}
.additional-video .more-zippy-toggle .icon {background-position:0px -32px!important;}
.additional-video .more-zippy-toggle:hover .icon {background-position:-16px -32px!important;}
.additional-video .goog-zippy-expanded .icon {background-position:0px -16px !important;}
.additional-video .goog-zippy-expanded:hover .icon {background-position:-16px -16px!important;}

.email-icon {background-position:0 0 !important;}

.email-icon:hover, a.email-link:hover .email-icon {background-position:-16px 0 !important;}

.video-details .icon {display:none!important;}

.quote-story .quotesnippet {background-color:#efefef!important;}

.quote-story .speaker-nub, .quote-story .info-nub {
  height:15px!important;
  width:23px!important;
  background-position:-1px -57px!important;
  }
  
#mc-left .quotesnippet {padding:9px!important;}  

.pull-quote-left, .pull-quote-right {
  height:20px!important; 
  width:25px!important;
  } 

.browse-links, #left-nav-sections { border-bottom-color: #efefef!important; }

.nav {padding-left:9px!important;}

.nav .nub-item {
  padding:2px 0!important;
  border-bottom:none!important;
  }

tr td.nub-item a,
.sidebar .filter-label, .sidebar .clustersearch-label   {
  color:#333!important;
  font-weight:700!important;
  background-color:transparent!important;
  }

tr td .selected {background-color:#fff!important;}

tr.selected td.nub-item a span,
tr td .selected a, tr.selected div.title  {color:#15395f!important;}

tr td .selected td:hover, tr.selected td {background-color:#fff!important;}
tr td .selected a:hover {cursor:default!important;}

.nav #t_FRONTPAGE .nub-item{border-top:none!important;}

.browse-sidebar .link {
  margin-bottom:0!important;
  text-align:left!important;
  }

#headline-wrapper{border-left:none!important;}

#notify-box .notify {background:#ed8e78!important;}

div.more-link:hover, div.fewer-link:hover {
  cursor:pointer!important;
  background-color:#E3E3E3!important;
  }

#pagination div {
  width:200px!important;
  background:url(data:image/png;base64,)!important;
  }

#pagination #start, #pagination #start-prev {width:79px!important;}

#pagination #end-next,
#pagination #end {
  background-position:-94px 0pt!important;
  margin-right:44px!important;
  width:107px!important;
  }
  
#pagination .o {
  background-position:-43px 0pt!important;
  width:15px!important;
  }  
  
 #pagination #current {
  background-position:-43px -26px!important;
  width:15px!important;
 }
  
.gwt-DialogBox-consumer {background-color:#ddd!important;}  
.gwt-DialogBox-consumer .Caption {background-color:#efefef!important;}

div.top-stories {border-top:3px solid #999!important;}

#personalize-open table .mod .bar {background:#999!important;}

#personalize-open .top-stories, .drag-mod .text, .mod .text {background:#efefef!important;}

.drag-mod .text, .mod .text  {color:#15395f!important;text-decoration:none!important;}

#personalize-open .column input {background:#ccc!important;}

.ac-renderer div.active {
  color:#fff!important;
  background:#15395f!important;
  }

.stock-tckr {
  background-position: -64px 0 !important;
  padding-left: 18px!important;
  }
  
.drag-mod .text, .mod .text {
  background-image: url(http://news.google.com/images/grip.gif)!important;
  background-repeat: no-repeat!important;
  text-indent:18px!important;
  }
}
