css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.google.com/reader/view/"), url-prefix("https://www.google.com/reader/view/") {

/* Resets default Mozilla styling of elements */
input, button, select, textarea, option {
-moz-appearance: none !important;
}

/* @group Core */

* {font-family: Helvetica, "Helvetica Neue", Helvetica-Neue, Helvetica, "MgOpen Moderna", sans-serif !important;}
body{ background-color:#f4f4f4 !important;}
#main{background-image:url(data:image/png;base64,) !important;background-position: left bottom !important;background-repeat:  repeat-x !important;}

#gbar nobr, #guser * { visibility:hidden; }
#gbar:hover nobr, #guser:hover * { visibility: visible; }
#guser { color: #f4f4f4 !important; }
#guser:hover  { color: #999 !important; }

/* Anything to to hide? */
#logo,.gbh, #gbar,
.scroll-filler-recs-message .secondary,
#directory-selector,
#recommendations-tree,
#lhn-recommendations,
#viewer-footer,
#friends-tree,
#chrome-view-links,
#trends-selector,
#entries-status,
.entry-likers,
#settings-link,
#search-restrict,
#search-submit,
#ogspacer,
.entry-actions .star,
.entry-title-go-to,
#chrome-title,
#chrome-lhn-toggle,
#lhn-selectors .selector-icon,
.lhn-hidden #search,
#gbg, .like   {display: none !important;}

/* Get rid of all unwanted borders and backgrounds */
#chrome-header, 
.card-actions,
#viewer-top-controls, 
#chrome-view-links, 
#viewer-header, 
#viewer-footer, 
#chrome-lhn-toggle, 
.lhn-section, 
#chrome, 
#viewer-container, 
#entries.list .entry, 
.collapsed,
#entries.list #current-entry.expanded,
#entries.list #current-entry.expanded .entry-actions,
.scroll-tree li,
.card, 
.card-bottom,
#search-restrict,
#chrome.page-view #viewer-page-container,
#footer  {
	background: none !important;
	border-color: transparent !important;
	-webkit-box-shadow: 0 0 0 !important;
}

/*begone yellow fiend*/

#directory-tour,
#featured-bundles-container,
#lhn-selectors .selector:hover,
.lhn-menu #lhn-add-subscription-section .goog-button-body:hover,
.friends-tree-notification-info,
.friends-feeds-invite-info,
#friends-tree .friends-tree-following-info,
.progress-message .message-area-inner,
.small-interruption,
.preview-interruption,
.friend-interruption.preview-interruption,
.bookmarklet-container .bookmarklet-callout-inner,
.recommended-feed-highlight,
.scroll-tree li a:hover,
.scroll-tree li .updated,
a:hover .tree-item-action-container,.scroll-tree li a.menu-open,
#featured-bundles-promo,
#explore-promo {
	background: rgba(255,255,255,0.6) !important;
}

/*remove buttons*/
.goog-button,.goog-button div{
	background: none !important;
	border-color: transparent !important;
	padding: 0 !important;
}

#entries-status{right: 36px !important;}

/*sprites*/

.scroll-tree .folder-icon, .scroll-tree .toggle, .lhn-button, .subscribe-button, .section-button, .tag .icon, .created-icon, .broadcast-icon {
	background-image: url(data:image/png;base64,) !important;
}

.entry .entry-actions .star,.entry .entry-actions .like,.entry .entry-actions .read-state,.entry .entry-actions .broadcast,.entry .entry-actions .broadcast-with-note,.entry .entry-actions .note-delete,.entry .entry-actions .email,.entry .entry-actions .tag,.entry .entry-icons .star {background-image: url(data:image/png;base64,) !important}

#loading-area .message-area-inner{background-color:#E91010 !important; color: #fff !important;}
#nav {margin: 0 0 0 20px !important;}
#nav li, #nav div {font-size: 12px !important;}

#nav:before {
	content:"Helvetireader";
	display: block;
	margin: -28px 0 50px 21px;
	color: #E91010;
	font-size: 14px;
	font-weight: bold;
	text-shadow: 0 1px 0 #fff;
}

.subscribe-button {
	margin: 0 0 0 -6px !important;
	background-position: -64px -81px !important;
	height: 20px !important;
}
.subscribe-button,
.folder-name-text,
.scroll-tree .name,
.selector .text,
a#sub-tree-subscriptions,
#recommendations-tree-view-all,
#chrome-lhn-menu .goog-button-body,
#friends-tree-item-0-name,
#footer a,
#friends-settings-link a,
.interruption p {
	color: #888 !important; 
	font-weight: bold !important;
	text-shadow: 0 1px 0 #fff;
}

/*****EDIT*****/

#mark-all-as-read {
padding:0 5px!important;
background:#fff!important;
border:1px solid #d1d1d1!important;
border-radius:3px!important;
-moz-border-radius:3px!important;
-webkit-border-radius:3px!important;
}

.entry-title {color: red !important;}

h2 {
color:#999!important;
}

#search-input {
color:#999!important;
font-weight:bold!important;
text-shadow: 0 1px 0 #fff;
}

#lhn-add-subscription  {
margin:5px 0 4px 10px!important;
}

#quick-add-bubble-holder {
border-radius:3px!important;
-moz-border-radius:3px!important;
-webkit-border-radius:3px!important;
}

#quick-add-bubble-holder form input {
background:#fff!important;
color:#888!important;
font:bold 13px Helvetica, "Helvetica Neue", Helvetica-Neue, Helvetica, "MgOpen Moderna", sans-serif !important;
margin-top:2px!important;
padding:4px!important;
border-radius:3px!important;
-moz-border-radius:3px!important;
-webkit-border-radius:3px!important;
}

#quick-add-bubble-holder form #quick-add-helptext {
font-size:0!important;
}

#quick-add-close {
background:url(data:image/png;base64,) no-repeat!important;
}

#quick-add-btn {
font:bold 12px Helvetica, "Helvetica Neue", Helvetica-Neue, Helvetica, "MgOpen Moderna", sans-serif !important;
text-shadow: 0 2px 0 #fff;
float:right!important;
margin:5px 8px 10px 0!important;
} 

.goog-button-body {
color:#999!important;
}

.goog-button-body:hover {
color:#777!important;
}

.entry-main .entry-original {
background:url(data:image/png;base64,) no-repeat!important;
}

.collapsed:hover {
background:#f0f0f0!important;
}

.message-area-inner, .message-area-text-container, .message-area-bottom-1, message-area-bottom-2, .message-area-bottom-3 {
background:#E91010!important;
color: #f4f4f4 !important;
}

/*****EDIT*****/

#sub-tree-header{color:#bbb !important;}
#friends-tree {margin: 0 0 0 15px !important;}
#friends-tree .link {padding: 0 0 0 10px!important;}
#friends-settings-link {padding: 0 0 0 33px!important;}
#sub-tree .folder-name-text {margin: 0 0 0 4px !important;}
#lhn-subscriptions { margin: 20px 0 0 0 !important;}
.unread-count { color: #E91010;}
.tree-link-selected { background: none !important; }
.tree-link-selected .name-text { color: #000 !important;}
#search {margin: 0!important;left: 40px !important;top: 80px !important;}
#search-input {border: 1px solid #ddd !important;-moz-border-radius: 8px!important;-webkit-border-radius: 10px;border-radius: 10px;width: 220px !important;}
a,a:visited,.link{color:#0061bf !important; text-decoration: none !important;}
#lhn-selectors .selected,#lhn-selectors .selected:hover{ background-color:transparent !important;}
.selected .link,.selected .text {color: #000 !important;font-weight: bold !important;}
#chrome { margin: -50px 0 10px 280px !important;}
.lhn-hidden #chrome { margin: -50px 0 10px 20px !important;}

/*cards*/
.entry-main {padding: 0 0 2em 0 !important;margin: 0 10px 0 35px !important;}
.entry .entry-body{padding: 20px 0 0 0 !important}
.entry { background: transparent !important;}
.entries-list .expanded {margin: 20px 0 !important;}
.card-content .entry-title {font-size: 20px !important;margin: 0 !important;}
.entry-author, .entry-author a, #overview .title a {font-weight: bold;color: #333 !important;font-size: 13px !important;}
.entry-date,.entry-actions .link, #sub-tree-subscriptions {color: #999 !important;}
.card-content .entry-date {
	float:none !important;	
	position: absolute;
	bottom: 0;
	font-size: 11px;
}
.item-body,.overview-metadata { line-height: 1.4; color: #333;}
#current-entry .entry-title-link {color: #d00 !important;}

.card,
#entries.list,
.friend-interruption,
#no-entries-msg, #home { 
	background: #f8f8f8 !important; 
	border: 1px solid #eee !important;	
	border-bottom: 1px solid #ddd !important;
	margin-right: 20px !important;	
	-moz-border-radius:6px !important;
	-webkit-border-radius:6px !important;
	border-radius:6px !important;
}

#current-entry .card, #no-entries-msg {background: #fff !important;border: 1px solid #ddd !important;border-bottom: 1px solid #ccc !important;}
#current-entry .entry-author a:hover { color: #E91010 !important; }
#viewer-footer {padding:3px 3px 3px 10px !important}
.card-content, #current-entry .card-content {padding:10px !important;}
.card-bottom { padding: 0px !important; }
.card-content .star{margin-top: 3px;}
.entry-title-link { color: #666 !important;}
.entry-comments { margin-left: 35px !important;}
.entry-actions {border-top: 1px solid #eee;padding: 8px 6px 8px 40px !important;font-size: 11px !important;}
.card-actions .link{margin: 0 5px 0 0!important;}
.friend-interruption {margin: 0 20px 0 10px !important;}
#overview .overview-segment{margin-bottom:2em !important;}
#no-entries-msg .friends-empty-msg{display:block;font-size:90%;margin-top:.5em}

/*Home view*/
#home {padding: 20px !important;}
#overview .item-title {font-weight: bold !important;font-size: 16px; display: block;color: #333!important;}
.overview-metadata{margin: 0 0 1em 0 !important;}
}

/* @end */
