css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org"), domain("craigslist.co.uk") {

/* Need Firefox 3.6+ For This to Work */
body {
background: -moz-linear-gradient(top, #292929 10%, #747474 100%) fixed !important;
/*background: #292929 !important; */   /* Comment Above and Uncomment This if You do NOT Have Firefox 3.6+ */
color: #CFCBC2 !important;
}

a {
color: #aaa !important;
text-align: center !important;
text-decoration: none !important;
outline: none !important;
text-transform: none !important;
}

a:hover {
text-decoration: none !important;
text-shadow: #ddd 1px 1px 16px !important;
}

/* Visited Links Will Be White With Soft Glow */
a:visited {
color: #eee !important;
text-shadow: #6EBCFF 1px 1px 16px !important;
}

a:visited:hover {
color: #44617a !important;
text-shadow: #000 1px 1px 16px !important;
}

/* Front Page Table */
table {
background-color: #2b2b2b !important;
color: #ddd !important;
width: 100% !important;
}

.wwl td a, #leftbar, #postlks li, #leftbar li, .hp #rightbar {
background-color: #2A2A2A!important;
background-repeat: no-repeat!important;
border:1px solid #44617A!important;
color: #6EBCFF!important;
font-size: 10px!important;
font-weight: bold!important;
padding: 5px 7px 5px 14px!important;
}

.www td a, table.city, .hp a, #leftbar td {
background: #333!important;
background-repeat: no-repeat!important;
color: #aaa !important;
-moz-border-radius: 3px !important
}

.www td a:hover, .hp a:hover {
background: #1a1a1a !important;
cursor: pointer !important;
text-shadow: none !important;
}

.hp * {
color: #eee !important;
}


.www b , .city b, .ban, #logo {
background: #2A2A2A!important;
border: 1px solid #44617a !important;
color: #eee !important;
cursor: pointer !important;
font-weight: bold!important;
-moz-border-radius: 3px !important
}

#topban div, .hp #leftbar, .hp #logo  {
background: #1a1a1a !important;
border: 1px solid #44617a !important;
color: #eee !important;
font-weight: bolder !important;
text-transform: uppercase !important;
-moz-border-radius: 8px 2px 8px 2px !important;
}

.hp #logo, .hp #postlks li, .hp #leftlinks li {
margin-left: -6px !important;
}

.hp #search {
margin-left: -2px !important;
}

blockquote {
background-color: transparent !important;
background: -moz-linear-gradient(top, #292929 40%, #090909 100%) fixed !important;  /* Comment and Use Below if NOT Using Firefox 3.6+ */
/*background: #292929 !important;*/
color: #eee !important;
text-transform: uppercase !important;
margin: 0px !important;
}

.bchead {
background: #111 !important;
border-color: #44617a !important;
-moz-border-radius: 8px 2px 8px 2px !important;
text-align: center !important;
}

#search div {
background: #1a1a1a!important;
border: 1px solid #44617a !important;
color: #eee !important;
font-weight: bold!important;
-moz-border-radius: 3px !important
}

.toc h4 {
text-align: center !important;
background: #1a1a1a !important;
-moz-border-radius: 8px 2px 8px 2px !important;
border: 3px solid #44617a !important;
height: 28px !important;
}

.hp .col ul li a, .hp a, .hp ul li a, .hp ul li a:hover {
background: transparent !important;
}

.hp .col ul li a {
color: #bbb !important;
}

.hp .col ul li {
background: #2a2a2a !important;
border-color: #44617a !important;
}

.hp .col ul li:hover, .hp ul li:hover {
background: #090909 !important;
color: #eee !important;
}

.hp .col h4, .hp .city h5, .hp #postlks li, .hp #leftlinks li {
background: #090909 !important;
-moz-border-radius: 6px 6px 0 0 !important;
}

.hp .city h5, .hp .col h4 {
border: 1px solid #44617a !important;
}

.hp #leftlinks li:hover, .hp #postlks li:hover {
background: #020202 !important;
}

.hp .col ul li a:hover {
text-decoration: none !important;
text-shadow: #999 0px 0px 1px, rgba(0,0,0,1) 1px 1px 1px, rgba(0,0,0,1) 1px 1px 3px, black 0px 0px 1px, white 0px 0px 2px !important;
color: #eee !important;
}

#flags {
background: #090909 !important;
border-color: #44617a !important;
-moz-border-radius: 8px !important;
margin-top: 28px !important;
}

#flags a.fl:hover {
background: #44617a !important;
-moz-border-radius: 4px !important;
color: #111 !important;
}

.toc #searchform {
background: #1a1a1a !important;
-moz-border-radius: 8px 2px 8px 2px !important;
border-color: #44617a !important;
}

.hp .city td {
background: #1a1a1a !important;
-moz-border-radius: 8px 8px 0 0 !important;
}

p {
background: #2a2a2a !important;
border: 1px solid #44617a !important;
-moz-border-radius: 8px 2px 8px 2px !important;
padding: 4px !important;
/*text-align: center !important; */   /* Uncomment to center align the postings */
}

p:hover {
background: #1a1a1a !important;
color: #ddd !important;
}

/* Move Postings Away from Left Edge */
.toc td, .toc a, .toc th, .toc .hl {
margin-left: 20px !important;  /* Comment if You Uncomment the Text Align Above */
}

#tsb {
background: #090909 !important;
-moz-border-radius: 8px 2px 8px 2px !important;
text-align: center !important;
border: 1px solid #44617a !important;
}

#sc #banner {
background: #191919 !important;
-moz-border-radius: 8px !important;
}

#sc #banner #logo span {
color: #ddd !important;
}

/* Forum IDs */
.anon {
color: #777 !important;
}

/* Misc Stuff */
.toc span.p, .toc .gc a {
color: #44617a !important;
background: #020202 !important;
border: 1px solid #44617a !important;
padding: 2px !important;
-moz-border-radius: 8px !important;
}

#sc #banner #prinfo {
background: #292929 !important;
-moz-border-radius: 8px !important;
border: 2px solid #44617a !important;
}

div#shareSocial {
background: #1a1a1a !important;
border-color: #44617a !important;
-moz-border-radius: 8px !important;
margin-top: 28px !important;
}

#list a {
margin-left: 20px !important;
}

fieldset, legend {
background: #131313 !important;
border: 2px solid #44617a !important;
-moz-border-radius: 6px !important;
}

legend {
padding: 8px !important;
margin-top: -18px !important;
}

tbody > tr > th {
background: #191919 !important;
}

tbody > tr > td {
color: #999 !important;
}

#pp .highlight, .highlight, .error, #pp .posting {
background: #393939 !important;
color: #eee !important;
text-shadow: #000 0px 0px 2px !important;
}

#pp span.req {
color: #44617a !important;
}

#pp input.req {
border-color: #191919 !important;
}

.hp #rightbar ul.menu li:hover {
background: transparent !important;
}

.hp #rightbar ul.menu li a:hover {
background: #090909 !important;
}

.hp #rightbar h5.hot {
background: #191919 !important;
}

.hp #rightbar h5.hot:hover {
background: #090909 !important;
}

.hp #rightbar ul.menu li.s:hover, .hp #rightbar ul.menu li.s a:hover {
color: #eee !important;
}

}
