@-moz-document domain("bash.org.ru") {
scrollbar[orient="vertical"] {
  cursor: default;
  -moz-appearance: none !important;}

thumb[orient="vertical"]{
  -moz-appearance: none !important;
  border: 0 none !important;}
thumb[orient="vertical"] *,scrollbarbutton[disabled="true"] {display: none !important;}

scrollbar[orient="vertical"] slider {background: transparent url("data:image/gif;base64,R0lGODdhAQABAIABAMDK1QAAACwAAAAAAQABAAACAkQBADs=") repeat-y left top !important;}

scrollbar[orient="vertical"][disabled="true"] {opacity: 0 !important;}

scrollbar[orient="vertical"] {
  width:16px;
  padding: 0px !important;
  margin: 0px -1px 0 0 !important;
  border: 0 none !important;
  background: #fafafa url("data:image/gif;base64,R0lGODdhAQABAIABAMDK1QAAACwAAAAAAQABAAACAkQBADs=") repeat-y right top !important;
}

thumb[orient="vertical"] {
  width:16px; background: #dedede !important;
  border: 1px solid !important;
  -moz-border-bottom-colors: #dedede !important;
  -moz-border-right-colors: #dedede !important;
  -moz-border-left-colors: #dedede !important;
  -moz-border-top-colors: #dedede !important;
  position: relative !important;
  z-index: 1 !important;}

thumb[orient="vertical"]:hover,thumb[orient="vertical"]:active {
  background: #aaaaaa !important;
  -moz-border-bottom-colors: #aaaaaa !important;
  -moz-border-right-colors: #aaaaaa !important;
  -moz-border-left-colors: #aaaaaa !important;
  -moz-border-top-colors: #aaaaaa !important;
  z-index: 10 !important;}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"],scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
  -moz-appearance: none !important;
  position: absolute;
  margin: -1px 0 !important;
  min-width: 16px;  width: 16px;  max-width: 16px;
  min-height: 16px;  height: 16px;  max-height: 16px;
  z-index: 1 !important;
  background: #dedede !important; border: 1px solid !important;
  border-style: solid solid solid !important;
  -moz-border-bottom-colors: #aaaaaa !important;
  -moz-border-right-colors: #aaaaaa !important;
  -moz-border-left-colors: #aaaaaa !important;
  -moz-border-top-colors: #aaaaaa !important;}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:hover,scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:hover {
  -moz-border-bottom-colors: #aaaaaa !important;
  -moz-border-right-colors: #aaaaaa !important;
  -moz-border-left-colors: #aaaaaa !important;
  -moz-border-top-colors: #aaaaaa !important;
  z-index: 10 !important;
  cursor: pointer !important;
  background: #aaaaaa no-repeat 4px 5px !important;}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
background-image: url("data:image/gif;base64,R0lGODlhBwAEAIAAAAAAAAAAACH5BAEKAAEALAAAAAAHAAQAAAIHjGEJq8sOCwA7") !important;  background-position: 4px 5px !important; background-repeat: no-repeat !important;}
scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:hover {
background-image: url("data:image/gif;base64,R0lGODlhBwAEAIAAAP///////yH5BAEKAAEALAAAAAAHAAQAAAIHjGEJq8sOCwA7") !important;}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] {
min-height: 17px;  height: 17px;  max-height: 17px;
background-image: url("data:image/gif;base64,R0lGODlhBwAEAIAAAAAAAAAAACH5BAEKAAEALAAAAAAHAAQAAAIIhA+BGWoNWSgAOw==") !important; background-position: 4px 6px !important; background-repeat: no-repeat !important;}
scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:hover {
background-image: url("data:image/gif;base64,R0lGODlhBwAEAIABAP///wAAACH5BAEKAAEALAAAAAAHAAQAAAIIhA+BGWoNWSgAOw==") !important; background-position: 4px 6px !important;}

textarea {padding: 0 0 0 3px !important;}
}
