css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

* {
font-family: Segoe UI;
font-size: 12px !important;
}

h1, h1>span, a.long-title>strong {
font-size: 21px !important;
padding: 5px 0 10px 0 !important;
}

DIV#watch-video-container {
padding-top: 10px !important;
padding-bottom: 10px !important;
background-color: #f4f4f4 !important;
}

!DIV#watch-video-container embed {
width: 853px !important;
height: 505px !important;
}

DIV#masthead-container, DIV#page {
border-bottom: 0 !important;
background-image: -moz-linear-gradient(90deg, #eaeaea, white) !important;
}

DIV.fm-title-underlined-gray, DIV.fm-title-underlined-blue, DIV.homepage-block-heading {
background-image: -moz-linear-gradient(90deg, white, #eaeaea) !important;
padding: 5px !important;
-moz-border-radius: 5px

}

DIV.fm-title-underlined-gray span a, SPAN#feed_promoted-titleText, DIV.comment-highlight-section-header, DIV.homepage-block-heading {
font-size: 16px !important;
font-weight: bold;
color: black !important;
}

DIV#masthead-container {
padding-bottom: 6px !important;
}

DIV#page.watch {
background-color: white !important;
background-image: none !important;
}

#logo {
margin-left: -5px !important;
background: url(data:image/png;base64,) !important;
}

BUTTON.flip.yt-uix-button.yt-uix-button-text {
margin-top: -4px !important;
font-size: 13px !important;
}

A.start {
display: none;
}

}
