/** Youtube - Right Side Description Panel **/

/** This CSS requires a script to work correctly: http://userscripts.org/scripts/show/73708 **/



@-moz-document domain("youtube.com") {





#watch-info:not(.expanded) {width:100px !important; display:inline !important; float:right !important; position:relative !important; top:-2px !important; right:1px !important;}

#watch-info:not(.expanded) + #watch-actions {width:540px !important;}

#watch-info.expanded #watch-views {margin-bottom:-56px !important;}

#watch-info.expanded #watch-views .watch-expander-body {display:none !important;}



#watch-description {margin-left:20px !important; background:#333333 !important; border:1px solid #CCCCCC !important; margin-bottom:10px !important;}

#watch-description .watch-expander-head {display:none !important;}

#watch-description .watch-expander-body {display:block !important; height:auto !important; padding:0 !important; margin:4px 6px !important; margin-top:2px !important;}

#watch-description .watch-expander-body #watch-description-body {display:block !important; visibility:visible !important; font-size:0 !important;}



#watch-description .watch-expander-body #watch-description-body * {font-size:12px !important;}

#watch-description .watch-description-username, #watch-description .watch-video-date {line-height:18px !important; display:block !important; margin-left:52px !important; padding-left:8px !important;}

#watch-description .watch-video-date {color:#333333 !important;}

#watch-description .watch-description-username-dash {display:none !important;}

#watch-description #watch-category {margin:0 !important;}

#watch-description #watch-category span, #watch-description #watch-tags span {color:#666666 !important; font-weight:normal !important; display:inline !important;}

#watch-description #watch-tags {padding-top:5px !important; margin:0 !important;}

#watch-description #watch-tags div {display:inline !important;}







}



/** END OF CSS **/
