css:
@namespace url(http://www.w3.org/1999/xhtml);

/*  top page  */
@-moz-document url("http://www.nicovideo.jp/") {
#PAGEHEADER > table[height="28"],
a[href="http://www.nicovideo.jp/?p=premium_top"],
.mb16p4+p[style="padding: 4px;"],
div[class="mb16p4"] div[class="thumb_frm"],
#PAGEFOOTER,
#PAGEHEADER+.mb16p4+div>div+div>div[class="mb16p4"]+script+table,
#web_pc_top,
#web_pc_top+script+script+p,
#PAGEBODY>.mb16p4,
#premium_btn,
#index_ads,
#category_recent>.mb16p4
{display:none !important;}
}


/*  all pages */
@-moz-document domain("nicovideo.jp") {
#PAGEHEADER > table[height="28"],
#PAGEHEADER+div[class="mb16p4"],
#PAGEFOOTER,
#web_pc_search,
#web_pc_tag,
#web_pc_my
{display:none !important;}
body{background-image:none !important;}
}

/*  movie page  */
@-moz-document url-prefix("http://www.nicovideo.jp/watch") {
#PAGEHEADER,
#WATCHHEADER>.mb16p4,
table[summary="ç™»éŒ²ã‚¿ã‚°"],
table[summary="ç™»éŒ²ã‚¿ã‚°"]+table,
#WATCHFOOTER,
#WATCHHEADER>.mb16p4+table>tbody>tr>td[width="100%"]+td,
img[src="http://res.nicovideo.jp/img/thumb/from_SMILEVIDEO.gif"],
p+p,
p+p+h1+p
{display:none !important;}
}
