/* lite mods */

/*
.cP .aC .no .nH .nH .no .nH:nth-child(2) div:nth-child(2) {
  height: 0 !important;
}
*/

body > :first-child > :nth-child(2) > .nH:first-child .nH:nth-child(2) > .no:first-child .nH.nn:nth-child(3) > .nH:first-child > .nH:first-child > .nH:first-child {
  margin-top: -25px !important;
}


.nH.l2.ov {
  position: relative !important;
  font-size: 85% !important;
}
.nH.l2.ov div {
  margin: 0 !important;
  padding: 0 !important;
  height: auto !important;
  overflow: hidden !important;
}
.nH.l2.ov .md {
  position:absolute !important;
  left: 15px !important;
  width: 10em !important;
  height: 1.2em !important;
  bottom: 0 !important;
  font-size: 70% !important;
}
.nH.l2.ov .md .mj {
  position:relative !important;
  top: -7.8em !important;
  display: block !important;
  width: 1px !important;
  margin: 0 !important;
  color: #999 !important;
  font-weight: normal !important;
  overflow: visible !important;
}

.nH .l6 {
  position: absolute !important;
  right: 15px !important;
  bottom: 0 !important;
}

.nH .mp {
  position: relative !important;
  left: -12em !important;
}

.nH .mn {
  display: none !important;
}

.nH .ma {
  display: none !important;
}






























/************************************************
 * GMail - Remove ads and relocate action links *
 * Version 0.9 - by lOtR 2008-2009              *
 ************************************************/

/****************
 * Hide the ads *
 ****************/

/* Ads Area */
.iY .Bu:last-child > .nH {
	height: 0px !important;
	overflow: hidden !important;
	width: 0px !important;
}

/* Separator column */
.iY .Bu:first-child + .Bu {
	display: none !important;
}

/* Message area */
.iY {
	width: 100% !important;
}

/*******************************************************
 * Display the action links on top of the message area *
 *******************************************************/

/* Title */
.iY .ha {
	margin-top: 25px !important;
}

/* Action Links Area (level 1) */
.iY .Bu:last-child .nH > .nH > .nH:first-child {
	position: absolute !important;
	left: 0px!important;
	top: 35px !important;
	width: 100% !important;
	line-height: 20px !important;
}

/* Action Links Area (level 2) */
.iY .Bu:last-child .hj {
	margin: 0px !important;
	float: right !important;
}

/* Action Link */
.hk {
	float: left !important;
	padding: 0px 10px 0px 0px !important;
}
.hk span {
	width: auto !important;
}



















































