@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body{
  color: #ddd !important;
  background: url('http://img153.imageshack.us/img153/662/pfc3.jpg') !important;
  background-position: top !important;
  background-attachment: fixed !important;
}

/* Div / Table / List Transparency Fixes
---------------------------------------*/
#watch-headline-container, #watch-video-container, #watch-headline h1, .video-stat, .video-messages, .groupnamecont, #content{
  background: none !important;
  color: #fff !important;
}

/* MasterHeader / Footer / Body
----------------------------*/
#masthead-container{
  background: none !important;
  border-bottom: 0px solid #444 !important;
}

#masthead #logo {
background: transparent url("http://img221.imageshack.us/img221/8690/yout.jpg") no-repeat !important;
}

.yt-button {
background: #121212 !important;
border: 0px solid #eee !important;
}

.yt-button:hover span, .yt-button {
color: #eee !important;
text-decoration: none !important;
}

#footer-container{ 
  background: none !important;
  border-Top: 0px solid #fff !important;
}

#footer, #region-picker, #language-picker, #safetymode-picker{
  border: none !important;
}

#baseDiv{
  padding-bottom:0px !important;
}

#picker-container{
  background: #333 !important;
  border: none !important;
}

/* Links 
--------------*/
a, .video-list-item .title, .opt-close-text, .iyt-edit-link,
.yt-uix-button-menu-item, .yt-button, .yt-uix-expander-head{
 color: #aaa !important;
}

/* Text
------------*/
.video-description b, .show-description b, .episode-description b, 
.channel-description b, .movie-description b, .show-long-description b, 
.show-short-description b, .show-mini-description b {
  color: #888 !important;
}


/*-------------------------------- Main Page -------------------------------------*/

/* Recommended / Being Watched / Most Popular
-----------------------------------------------*/
.feedmodule-anchor, #homepage-whats-new-block, .homepage-side-block{
  background: none !important;
}

#fyf_container{
  background: #333 !important;
  border: 1px solid #fff !important;
}

.fm-title-underlined-gray, .homepage-block-heading, #statmodules_inbox-title{
  background: #000 !important;
  border-bottom: 1px solid #fff !important;
  padding: 0px 0px 0px!important;
  color: #fff !important;
}

#iyt-login-suggest-side-box div div{
  background: #222 !important;
  border: 1px solid #222 !important;
}

#iyt-login-suggest-side-box div, .fyf-container-services , .statModule-item-line{
  background: #333 !important;
  border: none !important;
}

.feedmodule-data, .homepage-whatsnew-entry{
  padding-left: 5px !important;
}

.opt-banner, .opt-main{
  background: #444 !important;
  border: none !important;
  width: 99% !important;
}

.opt-box-top{
  visibility: hidden !important;
}

/*-------------------------------- Browse Page -------------------------------------*/

.browse-categories-side, .browse-tab-modifiers, .subcategory, .main-tabs-spotlight .video-entry , .hot-trends-cloud-area{
  background: #000 !important;
}

.browse-tab-modifiers .first, .main-tabs-spotlight-inner, .hot-trends-cloud-inner-area{
  background: #000 !important;
}

#browseMain .searchFooterBox div.yt-uix-pager, .browseAdditional .searchFooterBox div.yt-uix-pager {
  background: none !important;
  border-top: 1px solid #444 !important;
}

table.browse-modifiers-extended .browse-basic-modifiers .subcategory {
  background: none !important;
  color: #aaa !important;
}

/* Menu Links And Video Borders
-----------------------------------*/
table.browse-modifiers-extended .yt-menulink-menu a, table.browse-modifiers-extended .yt-menulink-menu, .yt-uix-button-menu .yt-uix-button-menu-item{
  background: #333 !important;
}

table.browse-modifiers-extended .yt-menulink-menu a:hover, .yt-uix-button-menu .yt-uix-button-menu-item:hover {
  background: #555 !important;
}

.video-thumb-220, .video-thumb-128, .video-thumb-120, .video-thumb-90, .video-thumb-50  {
  border: 1px solid #fff !important;
}

.yt-uix-button, .yt-uix-button:active, div.yt-uix-button-menu > table, yt-uix-button-menu-item{
  background: #444 !important;
  border: 1px solid #333 !important;
}

/*------------------------------ Search Page ------------------------------------*/
#results-main-content .show-result-types .result-type {
  color: #fff !important;
}

#search-duplicates-message{
  background: #333 !important;
  color: #ddd !important;
}

.searchFooterBox div {
  background: none !important;
}

.pyv-promoted-videos{
 background: #444 !important;
}

/*----------------------------- Video Page -------------------------------------*/

/* Vevo Pages Fix 
----------------------*/
#watch-branded-image-map{background: #333 url("http://img696.imageshack.us/img696/206/vevo20.jpg") no-repeat scroll center top !important;}
#watch-other-vids{background: none !important;}
#page.watch-branded #watch-main-container{background: none !important;}

/* Video Details Panel
--------------------------*/
#watch-info .yt-uix-expander-collapsed .watch-expander-head, #watch-actions-area{
  background-color: #222 !important;
  color: #ddd !important;
  border: 1px solid #444 !important;  
}

#watch-info{
  background: #333 !important;
  color: #ddd !important;
}

#watch-info .watch-expander-head, .watch-extra-info {
  background: #222 !important;
  color: #ddd !important;
  border: 1px solid #444 !important;
  -moz-border-radius:0px 0px 5px 5px;
}

/* Related Videos
--------------------------*/
#watch-related .title, #watch-sidebar .title{
  color: #ddd !important;
  font-weight: normal !important;
}

#watch-sidebar .watch-module-body {
  background: #000000 !important; 
  padding-top: 3px !important;  
  padding-left: 3px !important;
  border-left: 5px solid #000000 !important;
  color: #ddd !important; 
}

a.video-list-item-link:hover, a.video-list-item-link:active, .watch-ppv-vid, #subscribeMessage, .next-list-current{
  background: #444 !important; 
}

#watch-channel-discoverbox{
  background: #000 !important;
  border: 1px solid #fff !important;
}

/* Playlist / User Channel
---------------------*/
#watch-sidebar .watch-passive-list .watch-module-body{
  border-bottom: none !important;
}

#watch-sidebar .watch-active-list .watch-module-head, #watch-next-list-actions, #watch-sidebar .watch-module-expandable .watch-module-head{
  background: #fff !important;
  border: 2px solid #fff !important;
}
#watch-sidebar .watch-active-list.yt-uix-expander-collapsed .watch-module-body,
#watch-sidebar .watch-active-list .watch-module-body {
  border: none !important;
  border-left: 3px solid #000000 !important;
  border-right: 1px solid #000000 !important;
}

.watch-module-body-inner, #watch-sidebar .watch-active-list .video-list-item-link  {
 margin-left:0px !important;
}

/* Comments 
---------------------*/
#watch-discussion li.current{
  background: none !important;
  border: 1px solid #fff !important;
  border-left: 1px solid #fff !important;
}
#watch-discussion .current .wrapper{
  background: none !important;
}
#watch-discussion .time{
  visibility: visible !important;
}

#watch-discussion textarea, .watch-comments-post-count input {
  background: #333 !important;
  color: #fff !important; 
}

/*------------------------------- User Pages -------------------------------------------*/

/* Subscription Page 
-------------------------------*/
#videos, .video-stat span, #mymedia #list-pane .folder.selected, .actions{
  background: none !important;
  background-color: #000000 !important;
  color: #fff !important;
  border: none !important;
 }

.settings, #mymedia #view-pane .settings,  #mymedia #view-pane .splitter, .footer, #mymedia #list-pane .subfolder.selected, #mymedia #table #heading-filter, #mymedia #table #heading-check {
  background: none !important; 
  background-color: #000 !important;
  color: #bbb !important;
  border: none !important;
  }

/* My Videos
-------------------------------*/
#mymedia #table .video.even, #mymedia #table .video-details.even,  #compose-cell{
  background-color: #333 !important;
}
#mymedia #table .video.odd, #mymedia #table .video-details.odd{
  background-color: #444 !important;
}

/* Upload Videos
-------------------------------*/
.upload-outer-box, #instructions{
  background-color: transparent !important;
  border: 1px dotted #fff !important;
}
.upload-inner-box{
  background-color: #000 !important;
  border: none !important;
}

#hd-uploading-box, #about-uploading-box, #sponsored-videos-box, #instructions{
  color: #ddd !important;
}

/* Sign Up Form  
----------------------*/
.signup-table, .google-checkout{
  background: #282828 !important;
}

}
